import heapq
import xml.etree.ElementTree as ET

def parse_map(xml_string):
    root = ET.fromstring(xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])
    tiles = {}
    player_pos = None
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            tiles[(x, y)] = tile
            if tile.find('Player') is not None:
                player_pos = (x, y)
    return tiles, player_pos, width, height

def is_obstacle(tile):
    if tile is None: return True
    tile_type = tile.attrib['type']
    # VOID is a new type I've seen, adding it here.
    return tile_type in ['WALL', 'HEADBUTT_TREE', 'VOID', 'unseen'] or tile.find('Object') is not None

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def a_star_search(tiles, start, goal, width, height):
    neighbors = [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]

    if is_obstacle(tiles.get(goal)):
        potential_goals = []
        for dx, dy, move in neighbors:
            adj_tile_coord = (goal[0] + dx, goal[1] + dy)
            if not is_obstacle(tiles.get(adj_tile_coord)):
                potential_goals.append(adj_tile_coord)
        if not potential_goals:
            return None
        potential_goals.sort(key=lambda p: heuristic(start, p))
        goal = potential_goals[0]

    if is_obstacle(tiles.get(start)): return None

    close_set = set()
    came_from = {}
    gscore = {start: 0}
    fscore = {start: heuristic(start, goal)}
    oheap = []
    heapq.heappush(oheap, (fscore[start], start))

    while oheap:
        current = heapq.heappop(oheap)[1]
        if current == goal:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.reverse()
            return [start] + path

        close_set.add(current)
        for i, j, move_dir in neighbors:
            neighbor_coord = (current[0] + i, current[1] + j)

            if neighbor_coord not in tiles:
                continue

            neighbor_tile = tiles[neighbor_coord]

            if is_obstacle(neighbor_tile):
                continue

            # Correctly check for one-way ledges based on movement direction
            if move_dir == 'Up' and neighbor_tile.attrib.get('type') == 'LEDGE_HOP_DOWN':
                continue
            if move_dir == 'Left' and neighbor_tile.attrib.get('type') == 'LEDGE_HOP_RIGHT':
                continue
            if move_dir == 'Right' and neighbor_tile.attrib.get('type') == 'LEDGE_HOP_LEFT':
                continue

            tentative_g_score = gscore[current] + 1
            if neighbor_coord in close_set and tentative_g_score >= gscore.get(neighbor_coord, 0):
                continue

            if tentative_g_score < gscore.get(neighbor_coord, 0) or neighbor_coord not in [item[1] for item in oheap]:
                came_from[neighbor_coord] = current
                gscore[neighbor_coord] = tentative_g_score
                fscore[neighbor_coord] = tentative_g_score + heuristic(neighbor_coord, goal)
                heapq.heappush(oheap, (fscore[neighbor_coord], neighbor_coord))
    return None

