import xml.etree.ElementTree as ET
from collections import deque
import json

def find_reachable_unseen_tiles():
    root = ET.fromstring(map_xml_string)

    IMPASSABLE_TILE_TYPES = ['WALL', 'WATER', 'COUNTER', 'HEADBUTT_TREE', 'CUT_TREE', 'PC', 'MART_SHELF', 'VOID', 'BUOY', 'FLOOR_UP_WALL']
    OBSTACLE_MARKER_EMOJIS = ['üìç', '‚ò†Ô∏è']
    ONE_WAY_RULES = {
        'LEDGE_HOP_DOWN': (0, 1),
        'LEDGE_HOP_RIGHT': (1, 0),
        'LEDGE_HOP_LEFT': (-1, 0)
    }

    grid = {}
    tile_elements = {}
    start_x, start_y = -1, -1

    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            tile_elements[(x, y)] = tile
            grid[(x, y)] = {'type': tile.get('type'), 'seen': tile.get('seen')}
            if tile.find('Player') is not None:
                start_x, start_y = x, y

    if start_x == -1:
        print(json.dumps([]))
        return

    def is_passable(x, y):
        tile_info = grid.get((x, y))
        tile_element = tile_elements.get((x, y))
        if not tile_info or tile_info.get('type') in IMPASSABLE_TILE_TYPES:
            return False
        if tile_element:
          for marker in tile_element.findall('Marker'):
              if marker.get('emoji') in OBSTACLE_MARKER_EMOJIS:
                  return False
        return True

    potential_targets = set()
    for (x, y), tile_info in grid.items():
        if tile_info['seen'] == 'false':
            for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
                neighbor_x, neighbor_y = x + dx, y + dy
                if is_passable(neighbor_x, neighbor_y):
                    neighbor_info = grid.get((neighbor_x, neighbor_y))
                    if neighbor_info and neighbor_info['seen'] == 'true':
                        potential_targets.add((x, y))
                        break

    reachable_unseen_tiles = []
    for target_x, target_y in potential_targets:
        queue = deque([[(start_x, start_y)]])
        visited = {(start_x, start_y)}
        path_found = False
        while queue:
            path = queue.popleft()
            current_x, current_y = path[-1]

            if (current_x, current_y) == (target_x, target_y):
                reachable_unseen_tiles.append({'x': target_x, 'y': target_y})
                path_found = True
                break

            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                next_x, next_y = current_x + dx, current_y + dy
                neighbor_coord = (next_x, next_y)

                if neighbor_coord in visited:
                    continue

                if not is_passable(next_x, next_y) and neighbor_coord != (target_x, target_y):
                    continue

                can_move = True
                neighbor_type = grid.get(neighbor_coord, {}).get('type')
                if neighbor_type in ONE_WAY_RULES:
                    allowed_entry_dx, allowed_entry_dy = ONE_WAY_RULES[neighbor_type]
                    if (dx, dy) != (allowed_entry_dx, allowed_entry_dy):
                        can_move = False

                if can_move:
                    new_path = list(path)
                    new_path.append(neighbor_coord)
                    visited.add(neighbor_coord)
                    queue.append(new_path)
            if path_found:
                break

    print(json.dumps(reachable_unseen_tiles))

find_reachable_unseen_tiles()
