import collections
import xml.etree.ElementTree as ET
import json

def solve():
    try:
        tree = ET.fromstring(map_xml_string)
    except:
        print(json.dumps([]))
        return

    width = int(tree.get('width'))
    height = int(tree.get('height'))

    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])

    grid = {}
    for row in tree.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            t_type = tile.get('type')
            seen = tile.get('seen') == 'true'
            has_object = tile.get('has-object') == 'true'

            # Treat unseen as walkable for exploration. Include LADDER, PIER, and grass.
            is_walkable = t_type in ["FLOOR", "TALL_GRASS", "LONG_GRASS", "LADDER", "PIER", "WARP_CARPET", "WARP_CARPET_DOWN", "LEDGE_HOP_DOWN", "grass"] or not seen

            # Objects block unless it's the start or end tile
            if has_object and (x != end_x or y != end_y) and (x != start_x or y != start_y):
                is_walkable = False

            grid[(x, y)] = {"type": t_type, "walkable": is_walkable}

    queue = collections.deque([(start_x, start_y, [])])
    visited = {(start_x, start_y)}

    while queue:
        x, y, path = queue.popleft()
        if x == end_x and y == end_y:
            print(json.dumps(path))
            return

        for dx, dy, btn in [(0, -1, "Up"), (0, 1, "Down"), (-1, 0, "Left"), (1, 0, "Right")]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < width and 0 <= ny < height and (nx, ny) not in visited:
                tile = grid.get((nx, ny))
                if not tile: continue

                can_move = False
                if tile["walkable"]:
                    if tile["type"] == "LEDGE_HOP_DOWN":
                        if btn == "Down": # Only allow jumping down
                            can_move = True
                    else:
                        can_move = True

                if can_move:
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [btn]))
    print(json.dumps([]))

solve()
