import json
import xml.etree.ElementTree as ET

def solve():
    try:
        root = ET.fromstring(map_xml_string)
        width = int(root.attrib['width'])
        height = int(root.attrib['height'])

        start_x, start_y = None, None
        for row in root.findall("Row"):
            y = int(row.attrib['id'])
            for tile in row.findall("Tile"):
                if tile.find("Player") is not None:
                    start_x = int(tile.attrib['id'])
                    start_y = y
                    break
            if start_x is not None: break

        if start_x is None:
            print(json.dumps({"error": "Player not found on map"}))
            return

        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])

        grid = [[0 for _ in range(width)] for _ in range(height)]
        impassable_types = ["WALL", "BUOY", "WHIRLPOOL", "ROCK", "BOOKSHELF", "TV", "RADIO", "TOWN_MAP", "STATUE", "PIT"]

        for row in root.findall("Row"):
            y = int(row.attrib['id'])
            for tile in row.findall("Tile"):
                x = int(tile.attrib['id'])
                t_type = tile.attrib['type']
                is_warp = tile.attrib.get('is-warp') == 'true'
                has_object = tile.find("Object") is not None
                has_marker_obj = any(m.attrib.get('object_id') for m in tile.findall("Marker"))

                if t_type in impassable_types or ((has_object or has_marker_obj) and (x != target_x or y != target_y)) or (is_warp and (x != target_x or y != target_y)):
                    grid[y][x] = 1

        queue = [(start_x, start_y, [])]
        visited = {(start_x, start_y)}
        while queue:
            cx, cy, path = queue.pop(0)
            if cx == target_x and cy == target_y:
                print(json.dumps([{"x": start_x, "y": start_y}] + path))
                return
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = cx + dx, cy + dy
                if 0 <= nx < width and 0 <= ny < height and not grid[ny][nx] and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [{"x": nx, "y": ny}]))
        print(json.dumps({"error": "No path found"}))
    except Exception as e:
        print(json.dumps({"error": str(e)}))

solve()
