import xml.etree.ElementTree as ET
from collections import deque
import json

def find_path(map_xml_string, start, goal):
    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']

            # Treat objects as walls
            has_object = tile.attrib.get('has-object') == 'true'

            # Check markers for obstacles
            is_blocked_by_marker = False
            for marker in tile.findall('Marker'):
                label = marker.text.lower()
                if 'rock' in label or 'blocked' in label or 'ledge' in label:
                    is_blocked_by_marker = True

            # Collision logic
            if t_type in ['WALL', 'BUOY', 'DOOR'] or has_object or is_blocked_by_marker:
                grid[(x, y)] = 'WALL'
            elif t_type == 'LEDGE_HOP_DOWN':
                grid[(x, y)] = 'LEDGE'
            elif t_type == 'FLOOR_UP_WALL':
                grid[(x, y)] = 'UP_WALL'
            else:
                grid[(x, y)] = 'FLOOR'

    # Special case: if goal is a DOOR, allow it
    if goal in grid and root.find(f".//Row/Tile[@id='{goal[0]}'][@coordinate='({goal[0]}, {goal[1]})']").attrib['type'] == 'DOOR':
        grid[goal] = 'DOOR'

    queue = deque([(start, [])])
    visited = {start}

    while queue:
        (x, y), path = queue.popleft()

        if (x, y) == goal:
            return path

        for dx, dy, action in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = x + dx, y + dy

            if (nx, ny) not in grid:
                continue

            target = grid[(nx, ny)]

            if target == 'WALL':
                continue

            # Ledge mechanics: Can only go DOWN through it to the tile below
            if target == 'LEDGE':
                if action == 'Down':
                    nnx, nny = nx, ny + 1
                    if (nnx, nny) in grid and grid[(nnx, nny)] != 'WALL':
                        if (nnx, nny) not in visited:
                            visited.add((nnx, nny))
                            queue.append(((nnx, nny), path + [action]))
                continue

            # Up-wall mechanics: Impassable from the NORTH (can't move DOWN into it)
            if target == 'UP_WALL' and action == 'Down':
                continue

            if (nx, ny) not in visited:
                visited.add((nx, ny))
                queue.append(((nx, ny), path + [action]))

    return None

