import xml.etree.ElementTree as ET
import heapq
import json

def find_path(input_data):
    def parse_map(xml_string):
        root = ET.fromstring(xml_string)
        grid = {}
        player_pos = None
        for row_elem in root.findall('Row'):
            y = int(row_elem.get('id'))
            for tile_elem in row_elem.findall('Tile'):
                x = int(tile_elem.get('id'))
                tile_type = tile_elem.get('type')
                has_object = tile_elem.get('has-object') == 'true'
                is_warp = tile_elem.get('is-warp') == 'true'
                object_id_name = None
                obj_elem = tile_elem.find('Object')
                if obj_elem is not None:
                    object_id_name = obj_elem.get('id-name')
                if tile_elem.find('Player') is not None:
                    player_pos = (x, y)
                grid[(x, y)] = {'type': tile_type, 'has_object': has_object, 'is_warp': is_warp, 'object_id_name': object_id_name}
        return grid, player_pos, int(root.get('width')), int(root.get('height'))

    def has_hm(party, hm_name):
        for pokemon in party:
            if not pokemon.get('is_fainted', False):
                for move in pokemon.get('moves', []):
                    if move == hm_name:
                        return True
        return False

    def is_traversable(from_x, from_y, to_x, to_y, grid, party):
        if (to_x, to_y) not in grid: return False
        to_tile = grid[(to_x, to_y)]
        from_tile = grid[(from_x, from_y)]
        from_type = from_tile['type']
        to_type = to_tile['type']

        if to_type == 'impassable': return False
        if to_tile['has_object'] and not to_tile['is_warp'] and to_tile.get('object_id_name') != 'PIKACHU': return False

        valid_moves = {
            'ground': ['ground', 'grass', 'steps', 'cleared_boulder_barrier', 'open_gate', 'gate_offscreen', 'spinner_stop', 'teleport', 'hole', 'ladder_up', 'ladder_down'],
            'grass': ['ground', 'grass', 'steps'],
            'elevated_ground': ['elevated_ground', 'steps'],
            'steps': ['steps', 'ground', 'elevated_ground'],
            'water': ['water', 'ground', 'steps'],
            'ledge': [], 'cuttable': [],
            'spinner_up': ['ground', 'grass', 'steps', 'elevated_ground'],
            'spinner_down': ['ground', 'grass', 'steps', 'elevated_ground'],
            'spinner_left': ['ground', 'grass', 'steps', 'elevated_ground'],
            'spinner_right': ['ground', 'grass', 'steps', 'elevated_ground'],
            'spinner_stop': ['ground', 'grass', 'steps', 'elevated_ground']
        }

        if to_type in valid_moves.get(from_type, []): return True

        if to_type == 'ledge' and from_y == to_y - 1 and from_x == to_x: return True
        if to_type == 'cuttable' and has_hm(party, 'CUT'): return True
        if to_type == 'water' and from_type in ['ground', 'grass', 'steps'] and has_hm(party, 'SURF'): return True

        return False

    def get_neighbors(node, grid, party, map_width, map_height):
        x, y = node
        neighbors = []
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if 1 <= nx <= map_width and 1 <= ny <= map_height:
                if is_traversable(x, y, nx, ny, grid, party):
                    cost = 1
                    to_tile_type = grid.get((nx, ny), {}).get('type')
                    if to_tile_type == 'ledge' and dy == 1:
                        if is_traversable(nx, ny, nx, ny + 1, grid, party):
                           neighbors.append(((nx, ny + 1), cost))
                        continue 
                    neighbors.append(((nx, ny), cost))
        return neighbors

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    def a_star(start, goal, grid, party, map_width, map_height):
        original_goal = goal
        goal_tile = grid.get(goal)
        if not goal_tile or goal_tile['type'] == 'impassable' or (goal_tile['has_object'] and not goal_tile['is_warp'] and goal_tile.get('object_id_name') != 'PIKACHU'):
            found_new_goal = False
            for dx, dy in sorted([(0, 1), (0, -1), (1, 0), (-1, 0)], key=lambda p: heuristic((start[0]+p[0], start[1]+p[1]), original_goal)):
                new_goal_x, new_goal_y = original_goal[0] + dx, original_goal[1] + dy
                new_goal_tile = grid.get((new_goal_x, new_goal_y))
                if new_goal_tile and new_goal_tile['type'] != 'impassable' and not (new_goal_tile['has_object'] and not new_goal_tile['is_warp'] and new_goal_tile.get('object_id_name') != 'PIKACHU'):
                    goal = (new_goal_x, new_goal_y)
                    found_new_goal = True
                    break
            if not found_new_goal: return None

        frontier = [(0, start)]
        came_from = {start: None}
        cost_so_far = {start: 0}
        while frontier:
            _, current = heapq.heappop(frontier)
            if current == goal: break
            for next_node, cost in get_neighbors(current, grid, party, map_width, map_height):
                new_cost = cost_so_far[current] + cost
                if next_node not in cost_so_far or new_cost < cost_so_far[next_node]:
                    cost_so_far[next_node] = new_cost
                    priority = new_cost + heuristic(goal, next_node)
                    heapq.heappush(frontier, (priority, next_node))
                    came_from[next_node] = current
        else: return None
        path = []
        current = goal
        while current is not None: path.append(current); current = came_from.get(current)
        path.reverse()
        return path

    try:
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
        party = json.loads(input_data['party_data'])
        grid, player_pos, map_width, map_height = parse_map(map_xml_string)
        if player_pos is None:
            print(json.dumps({'error': 'Player position not found on the map.'}))
            return
        path_plan = a_star(player_pos, (target_x, target_y), grid, party, map_width, map_height)
        if path_plan:
            path_json = [{'x': x, 'y': y} for x, y in path_plan]
            print(json.dumps({'path_plan': path_json}))
        else:
            print(json.dumps({'error': 'No path found to the target.'}))
    except Exception as e:
        import traceback
        print(json.dumps({'error': f'An error occurred: {str(e)}', 'traceback': traceback.format_exc()}))

