import xml.etree.ElementTree as ET
import heapq
import json

def find_path(start_x, start_y, end_x, end_y, map_xml_string):
    root = ET.fromstring(map_xml_string)
    map_width = int(root.attrib['width'])
    map_height = int(root.attrib['height'])

    if not (0 <= start_x < map_width and 0 <= start_y < map_height and 0 <= end_x < map_width and 0 <= end_y < map_height):
        return []

    impassable_tiles = {'WALL', 'CUT_TREE', 'HEADBUTT_TREE', 'WATER', 'PC', 'BOOKSHELF', 'COUNTER', 'MART_SHELF', 'RADIO', 'TOWN_MAP', 'TV', 'WINDOW', 'VOID', 'unseen'}
    ledge_tiles = {'LEDGE_HOP_DOWN', 'LEDGE_HOP_RIGHT', 'LEDGE_HOP_LEFT'}

    grid = [[{'type': 'unseen', 'traversable': 0} for _ in range(map_width)] for _ in range(map_height)]

    for row_elem in root.findall('Row'):
        y = int(row_elem.attrib['id'])
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.attrib['id'])
            tile_type = tile_elem.attrib.get('type', 'unseen')
            grid[y][x]['type'] = tile_type

            if tile_type not in impassable_tiles:
                grid[y][x]['traversable'] = 1

            if tile_elem.find('Object') is not None:
                grid[y][x]['traversable'] = 0

    start_node = (start_x, start_y)
    end_node = (end_x, end_y)

    if grid[start_y][start_x]['traversable'] == 0:
        return []

    if grid[end_y][end_x]['traversable'] == 0:
        found_new_target = False
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor_x, neighbor_y = end_x + dx, end_y + dy
            if 0 <= neighbor_x < map_width and 0 <= neighbor_y < map_height and grid[neighbor_y][neighbor_x]['traversable'] == 1:
                end_node = (neighbor_x, neighbor_y)
                found_new_target = True
                break
        if not found_new_target:
            return []

    open_set = []
    heapq.heappush(open_set, (0, start_node))
    came_from = {}
    g_score = { (c_x, c_y): float('inf') for c_x in range(map_width) for c_y in range(map_height) }
    g_score[start_node] = 0
    f_score = { (c_x, c_y): float('inf') for c_x in range(map_width) for c_y in range(map_height) }
    f_score[start_node] = abs(start_node[0] - end_node[0]) + abs(start_node[1] - end_node[1])

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == end_node:
            path = []
            while current in came_from:
                path.append({'x': current[0], 'y': current[1]})
                current = came_from[current]
            path.append({'x': start_node[0], 'y': start_node[1]})
            return path[::-1]

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            nx, ny = neighbor

            if not (0 <= nx < map_width and 0 <= ny < map_height) or grid[ny][nx]['traversable'] == 0:
                continue

            neighbor_type = grid[ny][nx]['type']
            current_type = grid[current[1]][current[0]]['type']

            if neighbor_type == 'LEDGE_HOP_RIGHT' and current_type not in ledge_tiles and dx != 1:
                continue
            if neighbor_type == 'LEDGE_HOP_DOWN' and current_type not in ledge_tiles and dy != 1:
                continue
            if neighbor_type == 'LEDGE_HOP_LEFT' and current_type not in ledge_tiles and dx != -1:
                continue

            if current_type == 'LEDGE_HOP_RIGHT' and dx == -1:
                continue
            if current_type == 'LEDGE_HOP_DOWN' and dy == -1:
                continue
            if current_type == 'LEDGE_HOP_LEFT' and dx == 1:
                continue

            tentative_g_score = g_score[current] + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = g_score[neighbor] + abs(neighbor[0] - end_node[0]) + abs(neighbor[1] - end_node[1])
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

    return []

