import xml.etree.ElementTree as ET
import heapq
import json

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def reconstruct_path(came_from, current):
    path = []
    while current in came_from:
        path.append({'x': current[0], 'y': current[1]})
        current = came_from[current]
    path.append({'x': current[0], 'y': current[1]})
    return path[::-1]

root = ET.fromstring(map_xml_string)
width = int(root.attrib['width'])
height = int(root.attrib['height'])
player_x, player_y = -1, -1

for r in root.findall('Row'):
    for t in r.findall('Tile'):
        if t.find('Player') is not None:
            player_x = int(t.attrib['id'])
            player_y = int(r.attrib['id'])
            break
    if player_x != -1:
        break

impassable_tiles = {'WALL', 'WINDOW', 'CUT_TREE', 'SIGN', 'BOOKSHELF', 'BLACKBOARD', 'MART_SHELF', 'BUOY', 'TV', 'TOWN_MAP', 'BIRD', 'HEADBUTT_TREE', 'FRUIT_TREE', 'COMPUTER', 'PRINTER', 'VOID', 'WATER', 'CAVE', 'COUNTER'}
ledge_down = {'LEDGE_HOP_DOWN'}
ledge_right = {'LEDGE_HOP_RIGHT'}
ledge_left = {'LEDGE_HOP_LEFT'}
floor_up_wall = {'FLOOR_UP_WALL'}

grid = {}
for r in root.findall('Row'):
    y = int(r.attrib['id'])
    for t in r.findall('Tile'):
        x = int(t.attrib['id'])
        tile_type = t.attrib.get('type')
        is_impassable = tile_type in impassable_tiles or t.find('Object') is not None
        grid[(x, y)] = (tile_type, is_impassable)

def get_neighbors(x, y):
    neighbors = []
    current_tile_type, _ = grid.get((x, y), (None, True))
    for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
        nx, ny = x + dx, y + dy
        if not (1 <= nx <= width and 1 <= ny <= height):
            continue
        neighbor_tile_type, is_impassable = grid.get((nx, ny), (None, True))
        if is_impassable:
            continue
        if neighbor_tile_type in ledge_down and move != 'Down': continue
        if neighbor_tile_type in ledge_left and move != 'Left': continue
        if neighbor_tile_type in ledge_right and move != 'Right': continue
        if neighbor_tile_type in floor_up_wall and move != 'Up': continue
        if current_tile_type in floor_up_wall and move != 'Down': continue
        neighbors.append((nx, ny))
    return neighbors

