from collections import deque
import json

def run():
    try:
        root = ET.fromstring(map_xml_string)
        end_x = int(input_data['end_x'])
        end_y = int(input_data['end_y'])

        grid = {}
        player_pos = None
        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                if tile.find('Player') is not None:
                    player_pos = (x, y)
                grid[(x, y)] = {
                    'type': tile.attrib['type'],
                    'seen': tile.attrib['seen'] == 'true',
                    'has_object': tile.attrib.get('has-object') == 'true'
                }

        if not player_pos:
            print("Error: Player pos not found.")
            return

        def get_next_pos(x, y, dx, dy):
            cx, cy = x, y
            while True:
                nx, ny = cx + dx, cy + dy
                if (nx, ny) not in grid or not grid[(nx, ny)]['seen'] or grid[(nx, ny)]['type'] == 'WALL' or grid[(nx, ny)]['has_object']:
                    return cx, cy
                cx, cy = nx, ny
                if grid[(cx, cy)]['type'] != 'ICE':
                    return cx, cy

        queue = deque([(player_pos, [])])
        visited = {player_pos}

        while queue:
            (cx, cy), path = queue.popleft()
            if (cx, cy) == (end_x, end_y):
                print(json.dumps(path))
                return

            for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
                nx, ny = get_next_pos(cx, cy, dx, dy)
                if (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append(((nx, ny), path + [move]))
        print("No path found.")
    except Exception as e:
        print(f"Error: {str(e)}")

run()

