import xml.etree.ElementTree as ET
from collections import deque
import json

def run():
    try:
        root = ET.fromstring(map_xml_string)
        end_x = int(input_data['end_x'])
        end_y = int(input_data['end_y'])
        can_surf = input_data.get('can_surf', True)

        grid = {}
        player_pos = None
        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                if tile.find('Player') is not None:
                    player_pos = (x, y)

                has_obstacle = False
                if tile.attrib.get('has-object') == 'true':
                    has_obstacle = True

                grid[(x, y)] = {
                    'type': tile.attrib['type'],
                    'seen': tile.attrib['seen'] == 'true',
                    'has_obstacle': has_obstacle,
                    'is_warp': tile.attrib.get('is-warp') == 'true'
                }

        if not player_pos:
            print("Error: Player position not found.")
            return

        passable_types = ['FLOOR', 'TALL_GRASS', 'LONG_GRASS', 'WARP_CARPET', 'ICE', 'LADDER', 'PIT', 'WARP_CARPET_DOWN', 'WARP_CARPET_UP', 'WARP_CARPET_LEFT', 'WARP_CARPET_RIGHT']
        if can_surf:
            passable_types.append('WATER')

        queue = deque([(player_pos, [])])
        visited = {player_pos}

        while queue:
            (cx, cy), path = queue.popleft()
            if (cx, cy) == (end_x, end_y):
                print(json.dumps(path))
                return

            for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
                nx, ny = cx + dx, cy + dy
                if (nx, ny) in grid and (nx, ny) not in visited:
                    tile = grid[(nx, ny)]
                    if tile['seen'] and tile['type'] in passable_types:
                        # Allow entering pits if it's the destination, otherwise avoid them
                        if (nx, ny) == (end_x, end_y):
                            visited.add((nx, ny))
                            queue.append(((nx, ny), path + [move]))
                        elif not tile['has_obstacle'] and not tile['is_warp']:
                            visited.add((nx, ny))
                            queue.append(((nx, ny), path + [move]))
        print("No path found.")
    except Exception as e:
        print(f"Error: {str(e)}")

run()

