import xml.etree.ElementTree as ET
from collections import deque
import json

def run():
    try:
        root = ET.fromstring(map_xml_string)
        boulder_x = int(input_data['boulder_x'])
        boulder_y = int(input_data['boulder_y'])
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])

        grid = {}
        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                grid[(x, y)] = {
                    'type': tile.attrib['type'],
                    'seen': tile.attrib['seen'] == 'true',
                    'has_object': tile.attrib.get('has-object') == 'true'
                }

        passable_types = ['FLOOR', 'TALL_GRASS', 'LONG_GRASS', 'WATER', 'ICE', 'LADDER']

        queue = deque([( (boulder_x, boulder_y), [] )])
        visited = {(boulder_x, boulder_y)}

        while queue:
            (bx, by), path = queue.popleft()
            if (bx, by) == (target_x, target_y):
                print(json.dumps(path))
                return

            for dx, dy, move_name in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
                nbx, nby = bx + dx, by + dy
                px, py = bx - dx, by - dy

                if (nbx, nby) in grid and (nbx, nby) not in visited:
                    if (px, py) in grid:
                        ptile = grid[(px, py)]
                        if ptile['seen'] and ptile['type'] in passable_types:
                            if (nbx, nby) == (target_x, target_y):
                                visited.add((nbx, nby))
                                queue.append(( (nbx, nby), path + [{'push': move_name, 'boulder_to': (nbx, nby), 'player_at': (px, py)}] ))
                                continue

                            btile = grid[(nbx, nby)]
                            if btile['seen'] and btile['type'] in passable_types and not btile['has_object']:
                                visited.add((nbx, nby))
                                queue.append(( (nbx, nby), path + [{'push': move_name, 'boulder_to': (nbx, nby), 'player_at': (px, py)}] ))
        print("No solution found.")
    except Exception as e:
        print(f"Error: {str(e)}")

run()
