import json
import xml.etree.ElementTree as ET
from collections import deque

def solve():
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])

    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = [[True for _ in range(width)] for _ in range(height)]
    impassable_types = ['WALL', 'HEADBUTT_TREE', 'MART_SHELF', 'COUNTER', 'WATER', 'BOULDER', 'TABLE', 'PC', 'STAIRS_UP', 'STAIRS_DOWN', 'BOOKSHELF']

    start_pos = None

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib.get('type', 'unseen')

            if t_type in impassable_types:
                grid[y][x] = False

            if tile.attrib.get('has-object') == 'true':
                grid[y][x] = False

            for marker in tile.findall('Marker'):
                if 'Wall' in marker.text or 'ðŸ§±' in marker.attrib.get('emoji', ''):
                    grid[y][x] = False

            if tile.find('Player') is not None:
                start_pos = (x, y)
                grid[y][x] = True

    if start_pos is None:
        print(json.dumps([]))
        return

    queue = deque([(start_pos, [])])
    visited = {start_pos}

    if not grid[target_y][target_x]:
        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            nx, ny = target_x + dx, target_y + dy
            if 0 <= nx < width and 0 <= ny < height and grid[ny][nx]:
                target_x, target_y = nx, ny
                break
        else:
            print(json.dumps([]))
            return

    while queue:
        (cx, cy), path = queue.popleft()

        if (cx, cy) == (target_x, target_y):
            print(json.dumps(path))
            return

        for dx, dy, btn in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = cx + dx, cy + dy
            if 0 <= nx < width and 0 <= ny < height and grid[ny][nx] and (nx, ny) not in visited:
                visited.add((nx, ny))
                queue.append(((nx, ny), path + [btn]))

    print(json.dumps([]))

solve()

