import json

def fly_menu_navigator(menu_text, target_location):
    menu_lines = [line.strip() for line in menu_text.split('\n') if line.strip()]
    current_selection = None

    # The first line of the menu text usually contains the selected item
    # We need to extract the actual location name, which is before 'ââ'
    if menu_lines:
        first_line = menu_lines[0]
        if 'ââ' in first_line:
            current_selection = first_line.split('ââ')[0].replace('To ', '').strip()

    if current_selection and current_selection.upper() == target_location.upper():
        print(json.dumps(["A"])) # Select the item if it's the target
    else:
        print(json.dumps(["Down"])) # Otherwise, press Down to scroll

fly_menu_navigator(input_data['menu_text'], input_data['target_location'])

