import json

def parse_menu_items(menu_text):
    lines = menu_text.split('\n')
    items = []
    cursor_pos = -1

    for i, line in enumerate(lines):
        if 'â' in line:
            cursor_pos = len(items)
            clean_line = line.replace('â', '').strip()
        else:
            clean_line = line.strip()

        # Heuristic to filter out non-item lines or headers
        if clean_line and not any(keyword in clean_line for keyword in ['SWITCH', 'STATS', 'CANCEL', 'Bring out which', 'Choose a POKÃ©MON.', 'LV', 'HP', 'PP', 'TYPE/']):
            items.append(clean_line.split(' ')[0]) # Take only the first word as item name

    return items, cursor_pos

def get_pokemon_name(line):
    # Extract PokÃ©mon name, handling status like PAR, FNT
    if ' LV ' in line:
        parts = line.split(' LV ')[0].split(' ') # Get name before LV, split by spaces
        # Reconstruct name, ignoring status if it's the last word
        if parts[-1] in ['PAR', 'FNT', 'SLP', 'FRZ', 'PSN', 'BRN']:
            return ' '.join(parts[:-1]).strip()
        return ' '.join(parts).strip()
    return line.strip()

def parse_pokemon_menu_items(menu_text):
    lines = menu_text.split('\n')
    pokemon_items = []
    cursor_pos = -1

    for i, line in enumerate(lines):
        if 'â' in line:
            cursor_pos = len(pokemon_items)
            clean_line = line.replace('â', '').strip()
        else:
            clean_line = line.strip()

        # Filter out non-PokÃ©mon lines like headers or battle messages
        if 'LV ' in clean_line and 'HP:' not in clean_line and 'EXP:' not in clean_line:
            pokemon_name = get_pokemon_name(clean_line)
            if pokemon_name:
                pokemon_items.append(pokemon_name)
        elif 'Bring out which' in clean_line or 'Choose a POKÃ©MON.' in clean_line or 'âSWITCH' in clean_line or 'STATS' in clean_line or 'CANCEL' in clean_line:
            continue # Skip menu headers/footers
        elif clean_line and 'LV' not in clean_line: # Catch items without 'LV' but with actual text
            # This is a heuristic to catch other items that aren't PokÃ©mon
            # For now, let's assume if it's not a Pokemon line, we skip it for this specific function
            continue

    return pokemon_items, cursor_pos


input_data = json.loads(input_data)
menu_text = input_data['menu_text']
target_item = input_data['target_item']

buttons = []

# Determine if it's a PokÃ©mon selection menu or a general move/item menu
# Heuristic: If 'LV' appears frequently, it's likely a PokÃ©mon menu.
if 'LV' in menu_text and 'Choose a POKÃ©MON.' in menu_text:
    items, cursor_pos = parse_pokemon_menu_items(menu_text)
else:
    items, cursor_pos = parse_menu_items(menu_text)

try:
    target_index = items.index(target_item)
except ValueError:
    # Item not found on current screen, try scrolling (if applicable, though multi-page not fully implemented here)
    # For now, if not found, assume we need to scroll down to find it.
    # This simple logic assumes a single page or that target will eventually appear by scrolling.
    if items and target_item not in items: # if there are items and target is not among them, try moving down
        buttons.append('Down')
        print(json.dumps(buttons))
        exit()
    else:
        # If no items or target_item is genuinely not present (e.g., fainted pokemon)
        # This should ideally not happen if target_item is always valid
        print(json.dumps({'error': 'Target item not found and cannot scroll.'}))
        exit()


if cursor_pos == -1:
    # Cursor not found, try pressing down to reveal it or move
    buttons.append('Down')
elif cursor_pos < target_index:
    for _ in range(target_index - cursor_pos):
        buttons.append('Down')
elif cursor_pos > target_index:
    for _ in range(cursor_pos - target_index):
        buttons.append('Up')

# If the target is now at the cursor position, select it
if not buttons and cursor_pos == target_index:
    buttons.append('A')
elif buttons and items[cursor_pos] == target_item: # If we moved and landed on it, select it
    buttons.append('A')
elif not buttons and cursor_pos != target_index: # If cursor not found and not at target, just press A to see what happens
    buttons.append('A')

print(json.dumps(buttons))
