import collections
import xml.etree.ElementTree as ET
import json

def solve():
    try:
        tree = ET.fromstring(map_xml_string)
    except:
        print(json.dumps([]))
        return

    width = int(tree.get('width'))
    height = int(tree.get('height'))

    start_x = int(input_data['current_x'])
    start_y = int(input_data['current_y'])

    grid = {}
    unseen_tiles = []
    for row in tree.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            t_type = tile.get('type')
            seen = tile.get('seen') == 'true'
            has_object = tile.get('has-object') == 'true'

            walkable_types = [
                "FLOOR", "TALL_GRASS", "LONG_GRASS", "LADDER", "PIER", 
                "WARP_CARPET", "WARP_CARPET_DOWN", "WARP_CARPET_LEFT", 
                "WARP_CARPET_RIGHT", "WARP_CARPET_UP", "LEDGE_HOP_DOWN", "grass"
            ]

            is_walkable = t_type in walkable_types or not seen
            if has_object:
                is_walkable = False

            grid[(x, y)] = {"type": t_type, "walkable": is_walkable, "seen": seen}
            if not seen:
                unseen_tiles.append((x, y))

    queue = collections.deque([(start_x, start_y)])
    visited = {(start_x, start_y)}
    reachable_walkable = {(start_x, start_y)}

    while queue:
        x, y = queue.popleft()
        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            nx, ny = x + dx, y + dy
            if (nx, ny) in grid and (nx, ny) not in visited:
                tile = grid[(nx, ny)]
                if tile["walkable"] and tile["seen"]:
                    if tile["type"] == "LEDGE_HOP_DOWN":
                        if dy == 1:
                            visited.add((nx, ny))
                            reachable_walkable.add((nx, ny))
                            queue.append((nx, ny))
                    else:
                        visited.add((nx, ny))
                        reachable_walkable.add((nx, ny))
                        queue.append((nx, ny))

    reachable_unseen = []
    for ux, uy in unseen_tiles:
        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            px, py = ux + dx, uy + dy
            if (px, py) in reachable_walkable:
                reachable_unseen.append({"x": ux, "y": uy})
                break

    print(json.dumps(reachable_unseen))

solve()
