import json
import xml.etree.ElementTree as ET
from collections import deque

def solve():
    try:
        root = ET.fromstring(map_xml_string)
    except Exception:
        print("[]")
        return

    map_id = root.attrib.get('id')
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    player_x, player_y = -1, -1
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            if tile.find('Player') is not None:
                player_x = int(tile.attrib['id'])
                player_y = y
                break
        if player_x != -1:
            break

    if player_x == -1:
        print("[]")
        return

    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])

    impassable_types = {"WALL", "WATER", "HEADBUTT_TREE", "CUT_TREE", "BOOKSHELF", "PC", "TOWN_MAP", "WINDOW", "PIT"}
    # Known safe warps that are incorrectly labeled in XML
    safe_overrides = {("4_7", 6, 7)} 

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            has_obj = tile.attrib.get('has-object') == 'true'
            is_warp = tile.attrib.get('is-warp') == 'true'
            grid[(x, y)] = (t_type, has_obj, is_warp)

    queue = deque([(player_x, player_y, [{"x": player_x, "y": player_y}])])
    visited = {(player_x, player_y)}

    while queue:
        x, y, path = queue.popleft()

        if x == target_x and y == target_y:
            if input_data.get('autopress_buttons'):
                buttons = []
                for i in range(len(path) - 1):
                    dx = path[i+1]['x'] - path[i]['x']
                    dy = path[i+1]['y'] - path[i]['y']
                    if dx == 1: buttons.append("Right")
                    elif dx == -1: buttons.append("Left")
                    elif dy == 1: buttons.append("Down")
                    elif dy == -1: buttons.append("Up")
                print(json.dumps(buttons))
            else:
                print(json.dumps(path))
            return

        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < width and 0 <= ny < height and (nx, ny) not in visited:
                t_info = grid.get((nx, ny))
                if not t_info: continue
                t_type, has_obj, is_warp = t_info

                is_target = (nx == target_x and ny == target_y)
                is_safe_override = (map_id, nx, ny) in safe_overrides
                is_obstacle = (has_obj and not is_target) or (is_warp and not is_target and not is_safe_override) or (t_type == "PIT" and not is_target)
                is_passable = (t_type not in impassable_types and not is_obstacle) or t_type == "unseen" or is_target

                is_blocked = False
                curr_type, _, _ = grid[(x, y)]
                if "LEDGE_HOP" in curr_type:
                    if "DOWN" in curr_type and dy != 1: is_blocked = True
                    elif "UP" in curr_type and dy != -1: is_blocked = True
                    elif "LEFT" in curr_type and dx != -1: is_blocked = True
                    elif "RIGHT" in curr_type and dx != 1: is_blocked = True
                if "LEDGE_HOP" in t_type:
                    if "DOWN" in t_type and dy == -1: is_blocked = True
                    elif "UP" in t_type and dy == 1: is_blocked = True
                    elif "LEFT" in t_type and dx == 1: is_blocked = True
                    elif "RIGHT" in t_type and dx == -1: is_blocked = True

                if not is_blocked and is_passable:
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [{"x": nx, "y": ny}]))

    print("[]")
    return

solve()

