dungeon_navigator.python_script:
import json
import xml.etree.ElementTree as ET

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])

    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = {}
    for r in root.findall('Row'):
        y = int(r.get('id'))
        for t in r.findall('Tile'):
            x = int(t.get('id'))
            has_impassable_object = False
            obj_node = t.find('Object')
            if obj_node is not None and obj_node.get('id-name') != 'Pikachu':
                has_impassable_object = True
            grid[(x, y)] = {
                'type': t.get('type'),
                'has_object': has_impassable_object
            }

    impassable_types = ['impassable', 'unknown', 'cuttable', 'water', 'closed_gate']

    stack = [(start_x, start_y)]
    visited = set()
    path = []

    start_tile = grid.get((start_x, start_y))
    if not start_tile or start_tile['type'] in impassable_types or start_tile['has_object']:
        print(json.dumps({'path_found': False, 'error': 'Starting tile is impassable.'}))
        exit()

    while stack:
        cx, cy = stack.pop()

        if (cx, cy) in visited:
            continue

        visited.add((cx, cy))
        path.append({'x': cx, 'y': cy})

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = cx + dx, cy + dy

            if (nx, ny) in visited:
                continue

            if not (1 <= nx <= width and 1 <= ny <= height):
                continue

            neighbor_tile = grid.get((nx, ny))
            if not neighbor_tile or neighbor_tile['type'] in impassable_types or neighbor_tile['has_object']:
                continue

            if neighbor_tile['type'] == 'ledge':
                if dy == -1 or dx != 0:
                    continue

            stack.append((nx, ny))

    print(json.dumps({'path_found': True, 'path': path}))

except Exception as e:
    print(json.dumps({'path_found': False, 'error': str(e)}))

map_segment_analyzer.python_script:
import json
import xml.etree.ElementTree as ET
from collections import deque

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = {}
    all_warps = []
    all_objects = []
    for r in root.findall('Row'):
        y = int(r.get('id'))
        for t in r.findall('Tile'):
            x = int(t.get('id'))
            has_impassable_object = False
            obj_node = t.find('Object')
            if obj_node is not None and obj_node.get('id-name') != 'Pikachu':
                has_impassable_object = True
                all_objects.append({'id': obj_node.get('id'), 'id-name': obj_node.get('id-name'), 'name': obj_node.get('name'), 'x': x, 'y': y})
            grid[(x, y)] = {'type': t.get('type'), 'has_object': has_impassable_object}
            if t.get('is-warp') == 'true':
                all_warps.append({'x': x, 'y': y})

    impassable_types = ['impassable', 'unknown', 'cuttable', 'water']

    q = deque([(start_x, start_y)])
    visited = {(start_x, start_y)}
    reachable_tiles_count = 1
    reachable_warps = []
    reachable_objects = []

    while q:
        cx, cy = q.popleft()

        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            nx, ny = cx + dx, cy + dy
            if not (1 <= nx <= width and 1 <= ny <= height) or (nx, ny) in visited:
                continue

            neighbor_tile = grid.get((nx, ny))
            if not neighbor_tile:
                continue

            if neighbor_tile['type'] in impassable_types or (neighbor_tile['type'] == 'closed_gate') or neighbor_tile['has_object']:
                continue

            if neighbor_tile['type'] == 'ledge' and dy == -1:
                continue

            visited.add((nx, ny))
            q.append((nx, ny))
            reachable_tiles_count += 1

    for warp in all_warps:
        if (warp['x'], warp['y']) in visited:
            reachable_warps.append(warp)

    for obj in all_objects:
        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            adj_x, adj_y = obj['x'] + dx, obj['y'] + dy
            if (adj_x, adj_y) in visited:
                reachable_objects.append(obj)
                break

    print(json.dumps({'reachable_tiles_count': reachable_tiles_count, 'reachable_warps': reachable_warps, 'reachable_objects': reachable_objects}))
except Exception as e:
    print(json.dumps({'error': str(e)}))

pathfinder.python_script:
import json
import xml.etree.ElementTree as ET
from collections import deque

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    ignorable_types_str = input_data.get('ignorable_impassable_types', '')
    ignorable_types = [t.strip() for t in ignorable_types_str.split(',') if t.strip()] if ignorable_types_str else []

    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = {}
    for r in root.findall('Row'):
        y = int(r.get('id'))
        for t in r.findall('Tile'):
            x = int(t.get('id'))
            has_impassable_object = False
            obj_node = t.find('Object')
            if obj_node is not None and obj_node.get('id-name') != 'Pikachu':
                has_impassable_object = True
            grid[(x, y)] = {
                'type': t.get('type'),
                'has_object': has_impassable_object
            }

    base_impassable = ['impassable', 'unknown', 'cuttable', 'water']
    impassable_types = [t for t in base_impassable if t not in ignorable_types]
    if 'closed_gate' not in ignorable_types:
        impassable_types.append('closed_gate')

    queue = deque([([ (start_x, start_y) ])])
    visited = {(start_x, start_y)}

    while queue:
        path = queue.popleft()
        cx, cy = path[-1]

        if (cx, cy) == (end_x, end_y):
            json_path = [{'x': p[0], 'y': p[1]} for p in path]
            print(json.dumps({'path_found': True, 'path': json_path}))
            exit()

        current_tile = grid.get((cx, cy))

        if current_tile and current_tile['type'] == 'ledge':
            nx, ny = cx, cy + 1
            if (1 <= ny <= height):
                landing_x, landing_y = nx, ny + 1
                if (landing_x, landing_y) not in visited and (1 <= landing_y <= height):
                     landing_tile = grid.get((landing_x, landing_y))
                     if landing_tile and landing_tile['type'] not in impassable_types and not landing_tile['has_object']:
                        visited.add((landing_x, landing_y))
                        new_path = path + [(landing_x, landing_y)]
                        queue.append(new_path)
            continue

        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            nx, ny = cx + dx, cy + dy

            if not (1 <= nx <= width and 1 <= ny <= height) or (nx, ny) in visited:
                continue

            neighbor_tile = grid.get((nx, ny))
            if not neighbor_tile:
                continue

            if neighbor_tile['type'] in impassable_types or neighbor_tile['has_object']:
                continue

            if neighbor_tile['type'] == 'ledge':
                if dy == -1 or dx != 0:
                    continue

            visited.add((nx, ny))
            new_path = path + [(nx, ny)]
            queue.append(new_path)

    print(json.dumps({'path_found': False, 'path': []}))

except Exception as e:
    print(json.dumps({'path_found': False, 'error': str(e)}))

object_finder.python_script:
import json
import xml.etree.ElementTree as ET

try:
    object_name_to_find = input_data['object_name']
    root = ET.fromstring(map_xml_string)

    found_objects = []

    for r in root.findall('Row'):
        for t in r.findall('Tile'):
            for obj in t.findall('Object'):
                id_name = obj.get('id-name', '')
                event_name = obj.get('name', '')

                if object_name_to_find.lower() in id_name.lower() or object_name_to_find.lower() in event_name.lower():
                    found_objects.append({
                        'id-name': id_name,
                        'name': event_name,
                        'x': int(t.get('id')),
                        'y': int(r.get('id'))
                    })

    if found_objects:
        print(json.dumps({'found': True, 'objects': found_objects}))
    else:
        print(json.dumps({'found': False, 'message': f"Object '{object_name_to_find}' not found on the current map."}))

except Exception as e:
    print(json.dumps({'found': False, 'error': str(e)}))
