import xml.etree.ElementTree as ET
from collections import deque

def get_reachable_unseen(map_xml, start_x, start_y):
    root = ET.fromstring(map_xml)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = {}
    unseen = set()
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib.get('type', 'unseen')
            if t_type == 'unseen':
                unseen.add((x, y))
                grid[(x, y)] = False # Not walkable, but a target
            else:
                # Treat FLOOR, ICE, WARP_CARPET as walkable.
                walkable = t_type in ['FLOOR', 'ICE', 'WARP_CARPET_DOWN', 'WARP_CARPET_UP', 'WARP_CARPET_LEFT', 'WARP_CARPET_RIGHT']
                grid[(x, y)] = walkable

    queue = deque([(start_x, start_y)])
    visited = set([(start_x, start_y)])
    reachable_unseen = []

    while queue:
        x, y = queue.popleft()

        # Directions: Up, Down, Left, Right
        moves = [(0, -1), (0, 1), (-1, 0), (1, 0)]

        for dx, dy in moves:
            nx, ny = x + dx, y + dy
            if 0 <= nx < width and 0 <= ny < height:
                if (nx, ny) not in visited:
                    if (nx, ny) in unseen:
                        # Found an adjacent unseen tile
                        reachable_unseen.append((nx, ny))
                        visited.add((nx, ny)) # Don't re-add
                    elif grid.get((nx, ny), False):
                        visited.add((nx, ny))
                        queue.append((nx, ny))

    return f"Reachable Unseen Tiles: {reachable_unseen}"

