import xml.etree.ElementTree as ET
import collections
import json

# Fallback for argument injection failure
if 'start_x' not in locals():
    if 'input_data' in locals() and isinstance(input_data, dict):
        start_x = input_data.get('start_x')
        start_y = input_data.get('start_y')
        target_x = input_data.get('target_x')
        target_y = input_data.get('target_y')
        mode = input_data.get('mode', 'walking')

# Ensure mode has a default
if 'mode' not in locals():
    mode = 'walking'

def bfs(start, target, obstacles, width, height, ledges):
    queue = collections.deque([[start]])
    visited = set([start])

    while queue:
        path = queue.popleft()
        x, y = path[-1]

        if (x, y) == target:
            return path

        # Directions: Up, Down, Left, Right
        moves = [
            (0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')
        ]

        for dx, dy, direction in moves:
            nx, ny = x + dx, y + dy

            if 0 <= nx < width and 0 <= ny < height:
                if (nx, ny) in obstacles:
                    continue
                if (nx, ny) in visited:
                    continue

                # Check if we are moving FROM a ledge that blocks this direction
                if (x, y) in ledges:
                    ledge_dir = ledges[(x, y)]
                    if ledge_dir == 'DOWN' and direction != 'Down': continue
                    if ledge_dir == 'UP' and direction != 'Up': continue
                    if ledge_dir == 'LEFT' and direction != 'Left': continue
                    if ledge_dir == 'RIGHT' and direction != 'Right': continue

                # Check if we are moving INTO a ledge from the blocked side
                if (nx, ny) in ledges:
                    l_dir = ledges[(nx, ny)]
                    if l_dir == 'DOWN' and direction == 'Up': continue
                    if l_dir == 'UP' and direction == 'Down': continue
                    if l_dir == 'LEFT' and direction == 'Right': continue
                    if l_dir == 'RIGHT' and direction == 'Left': continue

                new_path = list(path)
                new_path.append((nx, ny))
                queue.append(new_path)
                visited.add((nx, ny))

    return None

try:
    # Parse XML
    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])
    obstacles = set()
    ledges = {}

    impassable_types = {'WALL', 'COUNTER', 'BOOKSHELF', 'PC', 'SIGN', 'TREE', 'ROCK', 'WINDOW'}
    if mode == 'walking':
        impassable_types.add('WATER')
        impassable_types.add('WHIRLPOOL')

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib.get('type', 'FLOOR')

            # Check type
            if t_type in impassable_types:
                obstacles.add((x, y))

            # Check objects (always obstacles for movement)
            has_object = tile.attrib.get('has-object', 'false') == 'true'
            if has_object:
                 obstacles.add((x, y))

            # Check ledges
            if 'LEDGE' in t_type:
                if 'DOWN' in t_type: ledges[(x, y)] = 'DOWN'
                elif 'UP' in t_type: ledges[(x, y)] = 'UP'
                elif 'LEFT' in t_type: ledges[(x, y)] = 'LEFT'
                elif 'RIGHT' in t_type: ledges[(x, y)] = 'RIGHT'

    # Run BFS
    path_coords = bfs((start_x, start_y), (target_x, target_y), obstacles, width, height, ledges)

    buttons = []
    if path_coords:
        for i in range(len(path_coords) - 1):
            cx, cy = path_coords[i]
            nx, ny = path_coords[i+1]
            if nx > cx: buttons.append('Right')
            elif nx < cx: buttons.append('Left')
            elif ny > cy: buttons.append('Down')
            elif ny < cy: buttons.append('Up')
        print(json.dumps(buttons))
    else:
        print(json.dumps([]))

except Exception as e:
    # Fail gracefully
    print(json.dumps([]))
