import xml.etree.ElementTree as ET
from collections import deque
import json

def find_reachable_unseen_tiles():
    root = ET.fromstring(map_xml_string)

    IMPASSABLE_TILE_TYPES = ['WALL', 'WATER', 'COUNTER', 'HEADBUTT_TREE', 'CUT_TREE', 'PC', 'MART_SHELF', 'VOID', 'BUOY']

    grid = {}
    player_pos = None
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            tile_type = tile.get('type')
            seen = tile.get('seen') == 'true'
            is_impassable = tile_type in IMPASSABLE_TILE_TYPES
            grid[(x, y)] = {'type': tile_type, 'seen': seen, 'impassable': is_impassable}
            if tile.find('Player') is not None:
                player_pos = (x, y)

    if not player_pos:
        print(json.dumps([]))
        return

    queue = deque([player_pos])
    visited = {player_pos}
    reachable_unseen_tiles = []

    while queue:
        current_x, current_y = queue.popleft()

        if not grid[(current_x, current_y)]['seen']:
            reachable_unseen_tiles.append({'x': current_x, 'y': current_y})

        for move_name, dx, dy in [('Up', 0, -1), ('Down', 0, 1), ('Left', -1, 0), ('Right', 1, 0)]:
            next_x, next_y = current_x + dx, current_y + dy

            if (next_x, next_y) in visited:
                continue

            next_tile_info = grid.get((next_x, next_y))

            if not next_tile_info or next_tile_info['impassable']:
                continue

            next_tile_type = next_tile_info.get('type')

            if (next_tile_type == 'FLOOR_UP_WALL' and move_name == 'Down') or (next_tile_type == 'LEDGE_HOP_DOWN' and move_name == 'Up'):
                continue

            visited.add((next_x, next_y))
            queue.append((next_x, next_y))

    output = sorted(reachable_unseen_tiles, key=lambda k: (k['y'], k['x']))
    print(json.dumps(output))

