import re
import json

def solve():
    puzzle_grid_raw = input_data['puzzle_grid_raw']

    targets = {
        1: (1, 1), 2: (2, 1), 3: (3, 1), 4: (4, 1),
        5: (1, 2), 6: (2, 2), 7: (3, 2), 8: (4, 2),
        9: (1, 3), 10: (2, 3), 11: (3, 3), 12: (4, 3),
        13: (1, 4), 14: (2, 4), 15: (3, 4), 16: (4, 4),
    }

    piece_locations = {}
    empty_slots = []
    items = re.findall(r'\[(Piece \d+|Empty) \((\d+),(\d+)\)\]', puzzle_grid_raw)

    for item in items:
        name, x_str, y_str = item
        x, y = int(x_str), int(y_str)
        if name.startswith('Piece'):
            piece_id = int(name.split(' ')[1])
            piece_locations[piece_id] = (x, y)
        else:
            empty_slots.append((x, y))

    moves = []

    pieces_to_move = []
    for piece_id in range(1, 17):
        if piece_locations.get(piece_id) != targets.get(piece_id):
            pieces_to_move.append(piece_id)

    for piece_id in pieces_to_move:
        current_pos = piece_locations.get(piece_id)
        if not current_pos: continue

        target_pos = targets[piece_id]

        occupying_piece_id = None
        for p_id, pos in piece_locations.items():
            if pos == target_pos:
                occupying_piece_id = p_id
                break

        if occupying_piece_id is not None:
            if not empty_slots:
                moves.append(f'Error: No empty slot to move blocking piece {occupying_piece_id}')
                continue

            temp_slot = empty_slots.pop(0)
            moves.append(f'Move piece {occupying_piece_id} from {target_pos} to temporary slot {temp_slot}')
            piece_locations[occupying_piece_id] = temp_slot
            empty_slots.append(target_pos)

        moves.append(f'Move piece {piece_id} from {current_pos} to target {target_pos}')
        piece_locations[piece_id] = target_pos
        if current_pos in piece_locations.values(): # Check if the old spot is now taken by another piece
             pass
        else:
             empty_slots.append(current_pos)
        if target_pos in empty_slots:
             empty_slots.remove(target_pos)

    print(json.dumps({'moves': moves}))

solve()
