import xml.etree.ElementTree as ET
import heapq

root = ET.fromstring(map_xml_string)
width = int(root.attrib['width'])
height = int(root.attrib['height'])
player_x, player_y = -1, -1

for r in root.findall('Row'):
    for t in r.findall('Tile'):
        if t.find('Player') is not None:
            player_x = int(t.attrib['id'])
            player_y = int(r.attrib['id'])
            break
    if player_x != -1:
        break

target_x = int(input_data['target_x'])
target_y = int(input_data['target_y'])

impassable_tiles = {'WALL', 'WINDOW', 'CUT_TREE', 'SIGN', 'BOOKSHELF', 'BLACKBOARD', 'MART_SHELF', 'BUOY', 'TV', 'TOWN_MAP', 'BIRD', 'HEADBUTT_TREE', 'FRUIT_TREE', 'COMPUTER', 'PRINTER', 'VOID', 'WATER', 'CAVE', 'CUT_08', 'CUT_28_GARBAGE', 'RADIO', 'INCENSE_BURNER'}

ledge_down = {'LEDGE_HOP_DOWN'}
ledge_right = {'LEDGE_HOP_RIGHT'}
ledge_left = {'LEDGE_HOP_LEFT'}
floor_up_wall = {'FLOOR_UP_WALL'}

grid = {}
for r in root.findall('Row'):
    y = int(r.attrib['id'])
    for t in r.findall('Tile'):
        x = int(t.attrib['id'])
        tile_type = t.attrib.get('type')
        is_impassable = tile_type in impassable_tiles or t.find('Object') is not None
        grid[(x, y)] = (tile_type, is_impassable)

def is_valid(x, y):
    return 1 <= x <= width and 1 <= y <= height and not grid.get((x, y), (None, True))[1]

def get_neighbors(x, y):
    neighbors = []
    for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
        nx, ny = x + dx, y + dy
        if 1 <= nx <= width and 1 <= ny <= height:
            tile_type, is_impassable = grid.get((nx, ny), (None, True))
            if is_impassable:
                continue

            current_tile_type, _ = grid.get((x, y), (None, True))

            if tile_type in ledge_down and move != 'Up': continue
            if tile_type in ledge_right and move != 'Left': continue
            if tile_type in ledge_left and move != 'Right': continue
            if tile_type in floor_up_wall and move != 'Up': continue

            neighbors.append((nx, ny))
    return neighbors

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

start_node = (player_x, player_y)

# Find a valid, adjacent target if the destination itself is impassable
target_node = (target_x, target_y)
if not is_valid(target_x, target_y):
    possible_targets = get_neighbors(target_x, target_y)
    if not possible_targets:
        print('No path found: Target is impassable and has no valid neighbors.')
        exit()
    target_node = min(possible_targets, key=lambda pos: heuristic(start_node, pos))

open_set = []
heapq.heappush(open_set, (0, start_node))

came_from = {}
g_score = {start_node: 0}
f_score = {start_node: heuristic(start_node, target_node)}

path_found = False
while open_set:
    _, current = heapq.heappop(open_set)

    if current == target_node:
        path = []
        while current in came_from:
            path.append({'x': current[0], 'y': current[1]})
            current = came_from[current]
        path.append({'x': start_node[0], 'y': start_node[1]})
        print(path[::-1])
        path_found = True
        break

    for neighbor in get_neighbors(current[0], current[1]):
        tentative_g_score = g_score[current] + 1
        if tentative_g_score < g_score.get(neighbor, float('inf')):
            came_from[neighbor] = current
            g_score[neighbor] = tentative_g_score
            f_score[neighbor] = g_score[neighbor] + heuristic(neighbor, target_node)
            if neighbor not in [i[1] for i in open_set]:
                heapq.heappush(open_set, (f_score[neighbor], neighbor))

if not path_found:
    print('No path found')

