import heapq
import xml.etree.ElementTree as ET

def pathfinder(input_data):
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])

    try:
        root = ET.fromstring(map_xml_string)
    except ET.ParseError as e:
        print(f"Error parsing map XML: {e}")
        return

    player_pos = None
    for row in root.findall('Row'):
        for tile in row.findall('Tile'):
            if tile.find('Player') is not None:
                player_coord = tile.get('coordinate')
                player_pos = tuple(map(int, player_coord.strip('()').split(', ')))
                break
        if player_pos:
            break

    if not player_pos:
        print("Player position not found on the map.")
        return

    start_node = player_pos
    goal_node = (target_x, target_y)

    impassable_types = ['WALL', 'WINDOW', 'CUT_TREE', 'SIGN', 'BOOKSHELF', 'BLACKBOARD', 'MART_SHELF', 'BUOY', 'TV', 'TOWN_MAP', 'BIRD', 'HEADBUTT_TREE', 'FRUIT_TREE', 'COMPUTER', 'PRINTER', 'VOID', 'WATER', 'CAVE', 'COUNTER']
    one_way_down = ['LEDGE_HOP_DOWN']
    one_way_right = ['LEDGE_HOP_RIGHT']
    one_way_left = ['LEDGE_HOP_LEFT']
    one_way_up = ['FLOOR_UP_WALL']

    grid = {}
    for row in root.findall('Row'):
        for tile in row.findall('Tile'):
            coord_str = tile.get('coordinate')
            coords = tuple(map(int, coord_str.strip('()').split(', ')))
            tile_type = tile.get('type')
            has_object = tile.find('Object') is not None
            grid[coords] = {'type': tile_type, 'has_object': has_object}

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    open_set = []
    heapq.heappush(open_set, (0, start_node))
    came_from = {}
    g_score = {start_node: 0}
    f_score = {start_node: heuristic(start_node, goal_node)}

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == goal_node:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start_node)
            path.reverse()

            moves = []
            for i in range(len(path) - 1):
                x1, y1 = path[i]
                x2, y2 = path[i+1]
                if x2 > x1: moves.append('Right')
                elif x2 < x1: moves.append('Left')
                elif y2 > y1: moves.append('Down')
                elif y2 < y1: moves.append('Up')
            print(','.join(moves))
            return

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)

            if neighbor not in grid:
                continue

            neighbor_tile = grid[neighbor]
            current_tile = grid[current]

            if neighbor_tile['type'] in impassable_types or neighbor_tile['has_object']:
                continue

            # One-way checks
            if current_tile['type'] in one_way_down and dy != 1: continue # Can only move down from this tile
            if current_tile['type'] in one_way_right and dx != 1: continue # Can only move right from this tile
            if current_tile['type'] in one_way_left and dx != -1: continue # Can only move left from this tile

            if neighbor_tile['type'] in one_way_down and dy != 1: continue # Can only move down onto this tile
            if neighbor_tile['type'] in one_way_right and dx != 1: continue # Can only move right onto this tile
            if neighbor_tile['type'] in one_way_left and dx != -1: continue # Can only move left onto this tile
            if neighbor_tile['type'] in one_way_up and dy != -1: continue # Can only move up onto this tile

            tentative_g_score = g_score[current] + 1

            if neighbor not in g_score or tentative_g_score < g_score[neighbor]:
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + heuristic(neighbor, goal_node)
                heapq.heappush(open_set, (f_score[neighbor], neighbor))

    print("No path found.")

pathfinder(input_data)
