import json
import xml.etree.ElementTree as ET

def find_path(target_x, target_y, map_xml):
    try:
        root = ET.fromstring(map_xml)
        width = int(root.get('width'))
        height = int(root.get('height'))

        start_x, start_y = None, None
        for row in root.findall('Row'):
            y = int(row.get('id'))
            for tile in row.findall('Tile'):
                if tile.get('has-player') == 'true' or tile.find('Player') is not None:
                    start_x = int(tile.get('id'))
                    start_y = y
                    break
            if start_x is not None: break

        if start_x is None: return []

        grid = {}
        for row in root.findall('Row'):
            y = int(row.get('id'))
            for tile in row.findall('Tile'):
                x = int(tile.get('id'))
                t_type = tile.get('type')
                has_obj = tile.get('has-object') == 'true'

                is_traversable = t_type in ['WATER', 'FLOOR', 'unseen', 'GRASS', 'SAND', 'DIRT', 'TALL_GRASS']
                if has_obj and (x, y) != (target_x, target_y):
                    is_traversable = False
                if t_type in ['WALL', 'BUOY', 'WHIRLPOOL', 'COUNTER', 'MART_SHELF', 'WINDOW']:
                    is_traversable = False

                grid[(x, y)] = is_traversable

        queue = [(start_x, start_y, [])]
        visited = set([(start_x, start_y)])

        while queue:
            cx, cy, path = queue.pop(0)
            if (cx, cy) == (target_x, target_y):
                return path

            for dx, dy, btn in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
                nx, ny = cx + dx, cy + dy
                if 0 <= nx < width and 0 <= ny < height and grid.get((nx, ny), False) and (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [btn]))
    except Exception as e:
        return []

    return []

