import xml.etree.ElementTree as ET
import heapq
import json

def find_path(start_x_str, start_y_str, end_x_str, end_y_str, temp_impassable_str=None):
    start_x, start_y = int(start_x_str), int(start_y_str)
    end_x, end_y = int(end_x_str), int(end_y_str)

    temp_impassable = set()
    if temp_impassable_str:
        try:
            impassable_coords = json.loads(temp_impassable_str)
            for item in impassable_coords:
                temp_impassable.add((item['x'], item['y']))
        except (json.JSONDecodeError, TypeError):
            pass

    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))

    grid = [[float('inf') for _ in range(map_width + 1)] for _ in range(map_height + 1)]
    traversable_types = ['ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'ladder_up', 'ladder_down']
    tile_types_dict = {}
    object_dict = {}

    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            tile_type = tile.get('type')
            tile_types_dict[(x, y)] = tile_type

            obj = tile.find('Object')
            if obj is not None:
                obj_name = 'Unknown Object'
                if 'name' in obj.attrib:
                    obj_name = obj.attrib['name']
                elif 'id-name' in obj.attrib:
                    obj_name = obj.attrib['id-name']
                object_dict[(x,y)] = obj_name

            if (x, y) in temp_impassable:
                grid[y][x] = float('inf')
                continue

            cost = float('inf')
            has_impassable_object = False
            is_pikachu_tile = False

            if obj is not None and 'id-name' in obj.attrib:
                if obj.attrib['id-name'] == 'Pikachu':
                    is_pikachu_tile = True
                else:
                    has_impassable_object = True

            if tile_type in traversable_types and not has_impassable_object:
                cost = 2 if is_pikachu_tile else 1

            grid[y][x] = cost

    start_node = (start_x, start_y)
    end_node = (end_x, end_y)

    if grid[end_y][end_x] == float('inf'):
        possible_goals = []
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = end_x + dx, end_y + dy
            if 1 <= nx <= map_width and 1 <= ny <= map_height and grid[ny][nx] != float('inf'):
                possible_goals.append((nx, ny))

        if not possible_goals:
            blocking_objects = []
            obj_name = object_dict.get((end_x, end_y), 'Unknown Object')
            blocking_objects.append({'x': end_x, 'y': end_y, 'name': obj_name, 'type': tile_types_dict.get((end_x, end_y), 'impassable')})
            print(json.dumps({'status': 'failure', 'reason': 'Destination and all adjacent tiles are impassable.', 'blocking_objects': blocking_objects}))
            return

        possible_goals.sort(key=lambda pos: abs(pos[0] - start_x) + abs(pos[1] - start_y))
        end_node = possible_goals[0]
        end_x, end_y = end_node

    open_set = [(0, start_node)]
    came_from = {}
    g_score = { (x,y): float('inf') for x in range(map_width + 1) for y in range(map_height + 1) }
    g_score[start_node] = 0
    f_score = { (x,y): float('inf') for x in range(map_width + 1) for y in range(map_height + 1) }
    f_score[start_node] = abs(start_x - end_x) + abs(start_y - end_y)

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == end_node:
            path = []
            while current in came_from:
                path.append({'x': current[0], 'y': current[1]})
                current = came_from[current]
            path.append({'x': start_node[0], 'y': start_node[1]})
            path.reverse()
            print(json.dumps({'status': 'success', 'path': path}))
            return

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            nx, ny = neighbor

            if not (1 <= nx <= map_width and 1 <= ny <= map_height):
                continue

            current_tile_type = tile_types_dict.get(current, 'impassable')
            neighbor_tile_type = tile_types_dict.get(neighbor, 'impassable')
            if neighbor_tile_type == 'ledge' and (dy == -1 or (dy == 0 and current_tile_type != 'ledge')):
                continue

            if grid[ny][nx] == float('inf'):
                continue

            tentative_g_score = g_score[current] + grid[ny][nx]
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                h_score = abs(nx - end_x) + abs(ny - end_y)
                f_score[neighbor] = tentative_g_score + h_score
                heapq.heappush(open_set, (f_score[neighbor], neighbor))

    print(json.dumps({'status': 'failure', 'reason': 'No path found. The start and end points may be in disconnected regions of the map.'}))

