import xml.etree.ElementTree as ET
import json
from collections import deque

def find_path_to_nearest_unseen(grid, start, debug_info):
    impassable_base = {
        'WALL', 'VOID', 'WATER', 'CUT_TREE', 'COUNTER', 'BUOY', 'HEADBUTT_TREE',
        'MART_SHELF', 'BOOKSHELF', 'TV', 'RADIO', 'PC', 'TOWN_MAP', 'WINDOW', 'PAINTING', 'STATUE', 'PILLAR',
        'BED', 'TABLE', 'CHAIR'
    }
    directional_warps = {'WARP_CARPET_DOWN', 'WARP_CARPET_LEFT', 'WARP_CARPET_RIGHT'}

    debug_info['impassable_base_types'] = list(impassable_base)
    debug_info['rejection_log'] = []
    debug_info['visit_log'] = []

    if start not in grid:
        debug_info['error'] = f'Start position {start} not in grid.'
        return None

    if grid.get(start, {}).get('type') in impassable_base:
        debug_info['error'] = f'Start position {start} is on an impassable tile type.'
        return None

    queue = deque([(start, [start])])
    visited = {start}

    while queue:
        current_pos, path = queue.popleft()
        debug_info['visit_log'].append(list(current_pos))

        current_type = grid.get(current_pos, {}).get('type')
        if current_type == 'unseen':
            return [list(p) for p in path]

        for move_x, move_y, move_name in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            next_pos = (current_pos[0] + move_x, current_pos[1] + move_y)

            rejection_reason = None
            if next_pos in visited:
                rejection_reason = 'Already visited'
            elif next_pos not in grid:
                rejection_reason = 'Out of bounds'
            else:
                next_type = grid.get(next_pos, {}).get('type')
                if next_type in impassable_base:
                    rejection_reason = f'Impassable type: {next_type}'
                elif current_type == 'LEDGE_HOP_DOWN' and move_name != 'Down': 
                    rejection_reason = f'Cannot move {move_name} from LEDGE_HOP_DOWN'
                elif current_type == 'LEDGE_HOP_LEFT' and move_name != 'Left': 
                    rejection_reason = f'Cannot move {move_name} from LEDGE_HOP_LEFT'
                elif current_type == 'LEDGE_HOP_RIGHT' and move_name != 'Right': 
                    rejection_reason = f'Cannot move {move_name} from LEDGE_HOP_RIGHT'
                elif current_type == 'FLOOR_UP_WALL' and move_name in ['Up', 'Down']:
                    rejection_reason = f'Cannot move {move_name} from FLOOR_UP_WALL'
                elif next_type == 'FLOOR_UP_WALL' and move_name != 'Up':
                    rejection_reason = 'Cannot enter FLOOR_UP_WALL except by moving Up'
                elif next_type == 'LEDGE_HOP_DOWN' and move_name == 'Up':
                    rejection_reason = f'Cannot move Up onto a LEDGE_HOP_DOWN tile'
                elif next_type == 'LEDGE_HOP_LEFT' and move_name == 'Right':
                    rejection_reason = f'Cannot move Right onto a LEDGE_HOP_LEFT tile'
                elif next_type == 'LEDGE_HOP_RIGHT' and move_name == 'Left':
                    rejection_reason = f'Cannot move Left onto a LEDGE_HOP_RIGHT tile'
                elif next_type in directional_warps:
                    rejection_reason = 'Directional warp'

            if rejection_reason:
                debug_info['rejection_log'].append(f'Rejected {next_pos} from {current_pos}: {rejection_reason}')
                continue

            visited.add(next_pos)
            new_path = list(path)
            new_path.append(next_pos)
            queue.append((next_pos, new_path))

    debug_info['error'] = 'No path to any unseen tile found.'
    return None

