import heapq

def find_path_a_star(grid, start, end, all_impassable, debug_info):
    impassable_base = {
        'WALL', 'VOID', 'WATER', 'CUT_TREE', 'COUNTER', 'BUOY', 'HEADBUTT_TREE', 'unseen',
        'MART_SHELF', 'BOOKSHELF', 'TV', 'RADIO', 'PC', 'TOWN_MAP', 'WINDOW', 'PAINTING', 'STATUE', 'PILLAR',
        'BED', 'TABLE', 'CHAIR'
    }
    directional_warps = {'WARP_CARPET_DOWN', 'WARP_CARPET_LEFT', 'WARP_CARPET_RIGHT'}

    debug_info['impassable_base_types'] = list(impassable_base)
    debug_info['impassable_coordinates_from_input'] = [list(coord) for coord in all_impassable]
    debug_info['rejection_log'] = []

    if start not in grid:
        debug_info['error'] = f'Start position {start} not in grid.'
        return None
    if end not in grid:
        debug_info['error'] = f'End position {end} not in grid.'
        return None

    if grid.get(start, {}).get('type') in impassable_base or start in all_impassable:
        debug_info['error'] = f'Start position {start} is on an impassable tile type or object.'
        return None

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = {pos: float('inf') for pos in grid}
    g_score[start] = 0
    f_score = {pos: float('inf') for pos in grid}
    f_score[start] = heuristic(start, end)

    while open_set:
        _, current_pos = heapq.heappop(open_set)

        if current_pos == end:
            path = []
            while current_pos in came_from:
                path.append(list(current_pos))
                current_pos = came_from[current_pos]
            path.append(list(start))
            return path[::-1]

        current_type = grid.get(current_pos, {}).get('type')

        for move_x, move_y, move_name in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            next_pos = (current_pos[0] + move_x, current_pos[1] + move_y)

            rejection_reason = None
            if next_pos not in grid: rejection_reason = 'Out of bounds'
            else:
                next_type = grid.get(next_pos, {}).get('type')
                if next_type in impassable_base: rejection_reason = f'Impassable type: {next_type}'
                elif next_pos in all_impassable and next_pos != end: rejection_reason = 'Impassable coordinate'
                elif current_type == 'LEDGE_HOP_DOWN' and move_name != 'Down': rejection_reason = f'Cannot move {move_name} from LEDGE_HOP_DOWN'
                elif current_type == 'LEDGE_HOP_LEFT' and move_name != 'Left': rejection_reason = f'Cannot move {move_name} from LEDGE_HOP_LEFT'
                elif current_type == 'LEDGE_HOP_RIGHT' and move_name != 'Right': rejection_reason = f'Cannot move {move_name} from LEDGE_HOP_RIGHT'
                elif current_type == 'FLOOR_UP_WALL' and move_name in ['Up', 'Down']: rejection_reason = f'Cannot move {move_name} from FLOOR_UP_WALL'
                elif next_type == 'FLOOR_UP_WALL' and move_name != 'Up': rejection_reason = 'Cannot enter FLOOR_UP_WALL except by moving Up'
                elif next_type == 'LEDGE_HOP_DOWN' and move_name == 'Up': rejection_reason = 'Cannot move Up onto a LEDGE_HOP_DOWN tile'
                elif next_type == 'LEDGE_HOP_LEFT' and move_name == 'Right': rejection_reason = 'Cannot move Right onto a LEDGE_HOP_LEFT tile'
                elif next_type == 'LEDGE_HOP_RIGHT' and move_name == 'Left': rejection_reason = 'Cannot move Left onto a LEDGE_HOP_RIGHT tile'
                elif next_type in directional_warps and next_pos != end: rejection_reason = 'Directional warp'

            if rejection_reason:
                debug_info['rejection_log'].append(f'Rejected {next_pos} from {current_pos}: {rejection_reason}')
                continue

            tentative_g_score = g_score[current_pos] + 1
            if tentative_g_score < g_score.get(next_pos, float('inf')):
                came_from[next_pos] = current_pos
                g_score[next_pos] = tentative_g_score
                f_score[next_pos] = tentative_g_score + heuristic(next_pos, end)
                if next_pos not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[next_pos], next_pos))

    debug_info['error'] = 'No path found to destination.'
    return None

