import xml.etree.ElementTree as ET
import math

def exploration_planner(map_xml_string, input_data):
    root = ET.fromstring(map_xml_string)
    player_pos = None
    for row_elem in root.findall('Row'):
        for tile_elem in row_elem.findall('Tile'):
            if tile_elem.get('has-player') == 'true':
                player_pos = (int(tile_elem.get('id')), int(row_elem.get('id')))
                break
        if player_pos:
            break

    if not player_pos:
        print("Exploration Planner Error: Player position not found.")
        return

    try:
        coords_str = input_data['coordinates']
        parts = [int(c.strip()) for c in coords_str.split(',')]
        target_coords = list(zip(parts[0::2], parts[1::2]))
    except (ValueError, KeyError, IndexError) as e:
        print(f"Exploration Planner Error: Invalid coordinates input. {e}")
        return

    unvisited = set(target_coords)
    path = []
    current_pos = player_pos

    while unvisited:
        def manhattan_distance(p1, p2):
            return abs(p1[0] - p2[0]) + abs(p1[1] - p2[1])

        nearest_coord = min(unvisited, key=lambda p: manhattan_distance(current_pos, p))
        path.append(nearest_coord)
        unvisited.remove(nearest_coord)
        current_pos = nearest_coord

    path_str = ','.join([f'({p[0]},{p[1]})' for p in path])
    print(f"Optimal exploration path: [{path_str}]")

exploration_planner(map_xml_string, input_data)

