import xml.etree.ElementTree as ET
import heapq

def pathfinder_func(map_xml_string, input_data):
    print("Pathfinder v5.3 starting...")
    try:
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
    except (ValueError, KeyError) as e:
        print(f"Pathfinder v5.3 Error: Invalid input data. {e}")
        return

    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))
    player_pos = None
    player_tile_type = None

    for row_elem in root.findall('Row'):
        for tile_elem in row_elem.findall('Tile'):
            if tile_elem.get('has-player') == 'true':
                player_pos = (int(tile_elem.get('id')), int(row_elem.get('id')))
                player_tile_type = tile_elem.get('type')
                break
        if player_pos:
            break

    if not player_pos:
        print('Pathfinder v5.3 Error: Player position not found in map data.')
        return

    print(f"Player at {player_pos}, Target at ({target_x}, {target_y})")

    is_surfing = player_tile_type == 'water'

    grid = [[0 for _ in range(map_width + 1)] for _ in range(map_height + 1)]
    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            is_pikachu = tile_elem.find("./Object[@id-name='Pikachu']") is not None
            has_object = tile_elem.find('Object') is not None
            is_warp = tile_elem.get('is-warp') == 'true'
            is_impassable = False

            impassable_types = ['impassable', 'boulder_barrier', 'closed_gate', 'unknown', 'gate_offscreen']

            if tile_type in impassable_types:
                is_impassable = True
            elif has_object and not is_pikachu:
                is_impassable = True
            elif is_surfing and tile_type != 'water':
                is_impassable = True
            elif not is_surfing and tile_type == 'water':
                is_impassable = True
            elif is_warp and (x, y) != (target_x, target_y):
                is_impassable = True

            if is_impassable:
                grid[y][x] = 1
            elif tile_type == 'ledge':
                grid[y][x] = 2
            else:
                grid[y][x] = 0

    start = player_pos
    end = (target_x, target_y)

    if not (1 <= end[0] <= map_width and 1 <= end[1] <= map_height):
        print(f"Pathfinder v5.3 Error: Target {end} is out of map bounds.")
        return

    if grid[end[1]][end[0]] == 1:
        original_end = end
        found_new_target = False
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_x, adj_y = original_end[0] + dx, original_end[1] + dy
            if 1 <= adj_x <= map_width and 1 <= adj_y <= map_height and grid[adj_y][adj_x] == 0:
                end = (adj_x, adj_y)
                found_new_target = True
                break
        if not found_new_target:
            print('Pathfinder v5.3 Error: Path not found. Target is impassable and has no traversable adjacent tiles.')
            return

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = { (c,r): float('inf') for r in range(map_height + 2) for c in range(map_width + 2) }
    g_score[start] = 0
    f_score = { (c,r): float('inf') for r in range(map_height + 2) for c in range(map_width + 2) }
    f_score[start] = heuristic(start, end)

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == end:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start)
            final_path_str = ','.join([f'({p[0]},{p[1]})' for p in path[::-1]])
            print(f'Path: [{final_path_str}]')
            print("Pathfinder v5.3 finished.")
            return

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)

            if not (1 <= neighbor[0] <= map_width and 1 <= neighbor[1] <= map_height):
                continue

            if grid[neighbor[1]][neighbor[0]] == 2:
                if dy == 1 and current[1] == neighbor[1] - 1:
                    actual_neighbor = (neighbor[0], neighbor[1] + 1)
                    if 1 <= actual_neighbor[1] <= map_height and grid[actual_neighbor[1]][actual_neighbor[0]] != 1:
                        tentative_g_score = g_score[current] + 1
                        if tentative_g_score < g_score.get(actual_neighbor, float('inf')):
                            came_from[actual_neighbor] = current
                            g_score[actual_neighbor] = tentative_g_score
                            f_score[actual_neighbor] = tentative_g_score + heuristic(actual_neighbor, end)
                            heapq.heappush(open_set, (f_score[actual_neighbor], actual_neighbor))
                continue

            if grid[neighbor[1]][neighbor[0]] == 1:
                continue

            tentative_g_score = g_score[current] + 1

            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + heuristic(neighbor, end)
                heapq.heappush(open_set, (f_score[neighbor], neighbor))

    print('Pathfinder v5.3 Error: Path not found.')
    print("Pathfinder v5.3 finished.")

pathfinder_func(map_xml_string, input_data)

