import xml.etree.ElementTree as ET
import json

def analyze_puzzle_state(map_xml_string):
    root = ET.fromstring(map_xml_string)
    puzzle_elements = {'gates': [], 'switches': []}
    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            if 'gate' in tile_type:
                puzzle_elements['gates'].append({'x': x, 'y': y, 'type': tile_type, 'state': tile_type.replace('_gate', '')})
            bg_object = tile_elem.find('Object[@name="PrintCinnabarQuiz"]')
            if bg_object is not None:
                puzzle_elements['switches'].append({'x': x, 'y': y, 'type': 'quiz_machine'})
    print(json.dumps(puzzle_elements, indent=2))

analyze_puzzle_state(map_xml_string)
