import xml.etree.ElementTree as ET
from collections import deque
import json
import sys

def find_path(xml_string, start_x, start_y, target_x, target_y):
    try:
        root = ET.fromstring(xml_string)
    except:
        return []
    width = int(root.get('width'))
    height = int(root.get('height'))
    grid = {}
    objects = set()

    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            t_type = tile.get('type')
            grid[(x, y)] = t_type
            if tile.find('Object') is not None:
                objects.add((x, y))
            for marker in tile.findall('Marker'):
                emoji = marker.get('emoji')
                if emoji in ['ğŸ‘¤', 'ğŸ“', 'ğŸª¨', 'ğŸ›Ÿ', 'ğŸ§±', 'ğŸš«']:
                    objects.add((x, y))

    def can_move(x, y, nx, ny, surfing):
        if not (0 <= nx < width and 0 <= ny < height): return False
        t_type = grid.get((nx, ny))
        if t_type is None or t_type == 'unseen': return True
        if (nx, ny) in objects and (nx, ny) != (target_x, target_y): return False

        # FLOOR_UP_WALL is a ledge face, impassable from all directions
        if t_type in ['WALL', 'BUOY', 'FLOOR_UP_WALL']: return False

        if surfing:
            if t_type != 'WATER': return False
        else:
            if t_type == 'WATER': return False

        curr_type = grid.get((x, y))
        dx, dy = nx - x, ny - y
        if t_type == 'LEDGE_HOP_DOWN' and dy != 1: return False
        if curr_type == 'LEDGE_HOP_DOWN' and dy != 1: return False

        return True

    initial_tile = grid.get((start_x, start_y))
    initial_surfing = (initial_tile == 'WATER')
    queue = deque([((start_x, start_y), [], initial_surfing)])
    visited = {((start_x, start_y), initial_surfing)}

    while queue:
        (x, y), path, surfing = queue.popleft()
        if (x, y) == (target_x, target_y):
            return path

        for dx, dy, btn in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
            nx, ny = x + dx, y + dy
            if (nx, ny, surfing) not in visited and can_move(x, y, nx, ny, surfing):
                visited.add((nx, ny, surfing))
                queue.append(((nx, ny), path + [btn], surfing))

        if not surfing:
            for dx, dy, btn in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
                nx, ny = x + dx, y + dy
                if grid.get((nx, ny)) == 'WATER' and (nx, ny, True) not in visited:
                    visited.add((nx, ny, True))
                    queue.append(((nx, ny), path + [btn, 'A', 'A'], True))
        else:
            for dx, dy, btn in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
                nx, ny = x + dx, y + dy
                t_type = grid.get((nx, ny))
                if t_type and t_type not in ['WATER', 'BUOY', 'WALL', 'unseen', 'FLOOR_UP_WALL'] and (nx, ny, False) not in visited:
                    if can_move(x, y, nx, ny, False):
                        visited.add((nx, ny, False))
                        queue.append(((nx, ny), path + [btn, 'A'], False))
    return []

