import json

def solve():
    root = ET.fromstring(map_xml_string)
    player_node = root.find(".//Player/..")
    if player_node is None:
        print(json.dumps([]))
        return

    start_x = int(player_node.attrib['id'])
    start_y = int(player_node.attrib.get('y', player_node.getparent().attrib['id'] if hasattr(player_node, 'getparent') else root.find(f".//Tile[@id='{start_x}']/..").attrib['id']))
    # Fallback for finding Y if structure is tricky
    for row in root.findall('Row'):
        for tile in row.findall('Tile'):
            if tile.find('Player') is not None:
                start_y = int(row.attrib['id'])
                break

    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            has_obj = tile.attrib.get('has-object') == 'true'
            # Impassable types
            if t_type in ['WALL', 'BUOY', 'FLOOR_UP_WALL'] or has_obj:
                walkable = False
            else:
                walkable = True
            grid[(x, y)] = (walkable, t_type)

    queue = deque([((start_x, start_y), [])])
    visited = {(start_x, start_y)}

    while queue:
        (x, y), path = queue.popleft()
        if (x, y) == (target_x, target_y):
            print(json.dumps(path))
            return

        for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = x + dx, y + dy
            if (nx, ny) in grid:
                walkable, t_type = grid[(nx, ny)]
                if walkable and (nx, ny) not in visited:
                    # Ledge check: only Down (dy=1)
                    if t_type == 'LEDGE_HOP_DOWN':
                        if dy == 1:
                            visited.add((nx, ny))
                            queue.append(((nx, ny), path + [move]))
                    else:
                        visited.add((nx, ny))
                        queue.append(((nx, ny), path + [move]))

    print(json.dumps([]))

