import re
import json

def parse_battle_screen(screen_text):
    player_pokemon = {}
    opponent_pokemon = {}
    lines = [line.strip() for line in screen_text.strip().split('\n')]

    try:
        # Opponent Name (Line 1) and Level (Line 2)
        opponent_pokemon['name'] = lines[0]
        level_match = re.search(r'LV\s*(\d+)', lines[1])
        if level_match:
            opponent_pokemon['level'] = int(level_match.group(1))

        # Player Name (Line 3) and HP (Line 4)
        player_pokemon['name'] = lines[2]
        hp_match = re.search(r'(\d+)/(\d+)', lines[3])
        if hp_match:
            player_pokemon['current_hp'] = int(hp_match.group(1))
            player_pokemon['max_hp'] = int(hp_match.group(2))
    except IndexError:
        pass

    result = {
        "player_active_pokemon": player_pokemon,
        "opponent_active_pokemon": opponent_pokemon
    }
    print(json.dumps(result))

parse_battle_screen(input_data['screen_text'])

