import xml.etree.ElementTree as ET
import heapq
import json

def find_path(start_x_str, start_y_str, end_x_str, end_y_str, temp_impassable_str=None):
    start_x, start_y = int(start_x_str), int(start_y_str)
    end_x, end_y = int(end_x_str), int(end_y_str)

    temp_impassable = set()
    if temp_impassable_str and temp_impassable_str != 'null':
        try:
            impassable_coords = json.loads(temp_impassable_str)
            for item in impassable_coords:
                temp_impassable.add((item['x'], item['y']))
        except (json.JSONDecodeError, TypeError):
