import xml.etree.ElementTree as ET
import json
import sys
from collections import deque

def path_and_execute(target_x_str, target_y_str):
    target_x = int(target_x_str)
    target_y = int(target_y_str)

    try:
        root = ET.fromstring(map_xml_string)
    except ET.ParseError as e:
        return f"Error parsing map_xml_string: {e}"

    map_width = int(root.get('width'))
    map_height = int(root.get('height'))

    player_pos = None
    grid = {}

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            grid[(x, y)] = tile_elem
            if tile_elem.find('Player') is not None:
                player_pos = (x, y)

    if not player_pos:
        print("Player position not found on the map.")
        return

    start_x, start_y = player_pos

    impassable_tile_types = {
        'WALL', 'WATER', 'VOID', 'PC', 'TV', 'COUNTER', 'BOOKSHELF', 
        'TOWN_MAP', 'POKEDEX', 'RADIO', 'PILLAR', 'MART_SHELF', 
        'INCENSE_BURNER', 'CUT_TREE', 'HEADBUTT_TREE', 'PIT'
    }

    original_target_coords = (target_x, target_y)
    target_tile = grid.get((target_x, target_y))
    target_is_impassable = False

    if target_tile is not None:
        tile_type = target_tile.get('type')
        seen = target_tile.get('seen') == 'true'
        has_object = target_tile.find('Object') is not None and target_tile.find('Player') is None
        if tile_type in impassable_tile_types or has_object or not seen:
            target_is_impassable = True

    if target_is_impassable:
        found_new_target = False
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = original_target_coords[0] + dx, original_target_coords[1] + dy
            if 1 <= nx <= map_width and 1 <= ny <= map_height:
                adj_tile = grid.get((nx, ny))
                if adj_tile is not None:
                    adj_type = adj_tile.get('type')
                    adj_seen = adj_tile.get('seen') == 'true'
                    adj_has_object = adj_tile.find('Object') is not None
                    if adj_type not in impassable_tile_types and not adj_has_object and adj_seen:
                        target_x, target_y = nx, ny
                        found_new_target = True
                        break
        if not found_new_target:
            print(f"Target ({original_target_coords[0]}, {original_target_coords[1]}) is impassable and no valid adjacent tile found.")
            return

    queue = deque([(start_x, start_y)])
    visited = set([(start_x, start_y)])
    parent_map = { (start_x, start_y): None }

    while queue:
        x, y = queue.popleft()

        if (x, y) == (target_x, target_y):
            path = []
            curr = (x, y)
            while curr is not None:
                path.append(curr)
                curr = parent_map[curr]
            path.reverse()
            path_of_moves = path[1:]
            print(json.dumps([{"x": px, "y": py} for px, py in path_of_moves]))
            return

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            next_x, next_y = x + dx, y + dy

            if not (1 <= next_x <= map_width and 1 <= next_y <= map_height):
                continue
            if (next_x, next_y) in visited:
                continue

            tile = grid.get((next_x, next_y))
            if tile is None:
                continue

            tile_type = tile.get('type')
            seen = tile.get('seen') == 'true'
            has_object = tile.find('Object') is not None and tile.find('Player') is None
            is_target_tile = (next_x, next_y) == (target_x, target_y)

            if not seen and not is_target_tile:
                continue
            if tile_type in impassable_tile_types:
                continue
            if has_object:
                continue

            current_tile = grid.get((x, y))
            if current_tile is None: continue
            current_tile_type = current_tile.get('type')

            # Rules for moving FROM a ledge
            if current_tile_type == 'LEDGE_HOP_DOWN' and dy != 1:
                continue
            if current_tile_type == 'LEDGE_HOP_LEFT' and dx != -1:
                continue
            if current_tile_type == 'LEDGE_HOP_RIGHT' and dx != 1:
                continue

            # Rules for moving ONTO a ledge
            if tile_type == 'LEDGE_HOP_DOWN' and (dy == -1 or dx != 0):
                continue
            if tile_type == 'LEDGE_HOP_LEFT' and (dx == 1 or dy != 0):
                continue
            if tile_type == 'LEDGE_HOP_RIGHT' and (dx == -1 or dy != 0):
                continue

            # Other one-way tiles
            if tile_type == 'FLOOR_UP_WALL' and dy == 1:
                continue

            visited.add((next_x, next_y))
            parent_map[(next_x, next_y)] = (x, y)
            queue.append((next_x, next_y))

    print(f"No path found to ({target_x}, {target_y}) from ({start_x}, {start_y})")

