import xml.etree.ElementTree as ET
import json

maps_data = json.loads(input_data['maps'])
graph = {}
map_names = {m['map_id']: ET.fromstring(m['map_xml']).get('name') for m in maps_data}

for map_info in maps_data:
    map_id = map_info['map_id']
    root = ET.fromstring(map_info['map_xml'])
    if map_id not in graph:
        graph[map_id] = []
    for warp in root.findall('.//Warp'):
        dest_map_id = warp.get('dest_map_id')
        if dest_map_id:
            graph[map_id].append(dest_map_id)

# Basic analysis for now, will be improved
start_map = maps_data[0]['map_id']
queue = [start_map]
visited = {start_map}
exploration_route = [map_names[start_map]]

while queue:
    current_map = queue.pop(0)
    for neighbor in graph.get(current_map, []):
        if neighbor not in visited:
            visited.add(neighbor)
            queue.append(neighbor)
            exploration_route.append(map_names[neighbor])

all_maps = set(map_names.keys())
unreachable = all_maps - visited

analysis = f"Reachable maps: {[map_names[m] for m in visited]}. "
if unreachable:
    analysis += f"Unreachable maps: {[map_names[m] for m in unreachable]}."
else:
    analysis += "All maps are connected."

print(json.dumps({'connectivity_analysis': analysis, 'optimal_exploration_route': exploration_route}))

