import json
import re
import xml.etree.ElementTree as ET

def manual_path_planner_v6(start_x, start_y, directions_str, map_xml_string):
    root = ET.fromstring(map_xml_string)
    map_width = int(root.attrib['width'])
    map_height = int(root.attrib['height'])

    impassable_types = {
        'WALL', 'COUNTER', 'MART_SHELF', 'PC', 'BOOKSHELF', 'HEADBUTT_TREE', 'CUT_TREE',
        'TV', 'TOWN_MAP', 'WINDOW', 'ROCK', 'BUOY', 'VOID', 'WATER', 'SEA'
    }

    grid = [[0 for _ in range(map_width + 1)] for _ in range(map_height + 1)]
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            if tile.attrib.get('type') in impassable_types:
                grid[y][x] = 1
            if tile.find('Object') is not None:
                grid[y][x] = 1

    path = [{'x': start_x, 'y': start_y}]
    current_x, current_y = start_x, start_y
    moves = re.findall(r'([UDRL])(\d+)', directions_str.upper())

    for direction, count_str in moves:
        count = int(count_str)
        for _ in range(count):
            next_x, next_y = current_x, current_y
            if direction == 'U':
                next_y -= 1
            elif direction == 'D':
                next_y += 1
            elif direction == 'L':
                next_x -= 1
            elif direction == 'R':
                next_x += 1

            if not (1 <= next_x <= map_width and 1 <= next_y <= map_height and grid[next_y][next_x] == 0):
                print(json.dumps(path))
                return

            current_x, current_y = next_x, next_y
            path.append({'x': current_x, 'y': current_y})

    print(json.dumps(path))

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    directions = input_data['directions']
    manual_path_planner_v6(start_x, start_y, directions, map_xml_string)
except Exception as e:
    print(json.dumps(f"Error: {str(e)}"))
