import json
import re

def parse_screen(screen_text):
    lines = screen_text.split('\n')
    items = []
    cursor_pos = -1
    pocket = "Items" # Default pocket

    pocket_match = re.search(r'Pocket: \[(.*?)\]', screen_text)
    if pocket_match:
        pocket = pocket_match.group(1)

    item_lines_started = False
    for i, line in enumerate(lines):
        if "---------------------" in line:
            item_lines_started = True
            continue
        if not item_lines_started:
            continue
        if "Pocket:" in line:
            continue

        match = re.match(r'\s*(▶)?([A-Z][A-Z\s-]*?)\s*×\s*\d+', line.strip())
        if not match:
            match = re.match(r'\s*(▶)?([A-Z][A-Z\s-]+)', line.strip())

        if match:
            is_cursor = match.group(1) is not None
            item_name = match.group(2).strip()

            if item_name in ["MAP", "BACK", "▲", "▼"]:
                continue

            items.append(item_name)
            if is_cursor:
                cursor_pos = len(items) - 1

    return items, cursor_pos, pocket

def solve():
    target_item_name = input_data['target_item_name']
    current_screen_text = input_data['current_screen_text']
    # The state object is no longer used as the required output format cannot persist it.

    items, cursor_pos, current_pocket = parse_screen(current_screen_text)

    # Pocket navigation logic
    pockets = ["Items", "Balls", "Key Items", "TM/HM"]
    target_pocket = "Items"
    if "BALL" in target_item_name:
        target_pocket = "Balls"
    elif target_item_name in ["BICYCLE", "BLUE CARD", "COIN CASE", "GOOD ROD", "ITEMFINDER", "SQUIRTBOTTLE"]:
        target_pocket = "Key Items"
    elif "HM" in target_item_name or "TM" in target_item_name:
        target_pocket = "TM/HM"
    else:
        target_pocket = "Items"

    if current_pocket != target_pocket:
        current_idx = pockets.index(current_pocket)
        target_idx = pockets.index(target_pocket)

        diff = target_idx - current_idx
        buttons = []
        if diff > 0:
            for _ in range(diff):
                buttons.append("Right")
        else:
            for _ in range(abs(diff)):
                buttons.append("Left")

        print(json.dumps(buttons))
        return

    # Item selection logic
    if target_item_name in items:
        target_pos = items.index(target_item_name)

        num_items = len(items)
        forward = (target_pos - cursor_pos + num_items) % num_items
        backward = (cursor_pos - target_pos + num_items) % num_items

        buttons = []
        if forward <= backward:
            for _ in range(forward):
                buttons.append("Down")
        else:
            for _ in range(backward):
                buttons.append("Up")

        buttons.append("A")

        print(json.dumps(buttons))
        return

    # Scrolling logic: If the item is not visible, scroll down to find it.
    print(json.dumps(["Down"]))

solve()

