import json
import re

current_screen_text = input_data['current_screen_text']
move_to_select = input_data['move_to_select']

lines = current_screen_text.split('\n')
moves = []
cursor_index = -1

# The screen text for this game's battle UI does not include PP counts on the same line as the move names.
# This makes parsing based on 'PP' unreliable. A more robust method is to find the move box visually.
# The move list is consistently displayed in a box with 4 move slots.

# A reliable anchor is the line with the player's Pokémon info, which is just above the move box.
move_lines = []
found_move_box = False
for i, line in enumerate(lines):
    # This pattern matches the line containing the move list, like "│   │▶QUICK ATTACK │"
    if re.search(r'│   │.*[A-Z]', line):
        # We've found the start of the move box. The next 4 lines should be the moves.
        # We need to handle cases where there are fewer than 4 moves.
        for j in range(i, len(lines)):
            sub_line = lines[j]
            if re.search(r'│   │.*[A-Z]', sub_line):
                move_lines.append(sub_line)
            else:
                break # Stop when we hit a line that doesn't match the move format
        found_move_box = True
        break

if found_move_box:
    for line in move_lines:
        clean_line = line.replace('▶', '').strip()
        match = re.search(r'([A-Z][A-Z -]*)', clean_line)
        if match:
            move_name = match.group(1).strip()
            if move_name:
                moves.append(move_name)
                if '▶' in line:
                    cursor_index = len(moves) - 1

if not moves or move_to_select not in moves or cursor_index == -1:
    print(json.dumps([]))
else:
    target_index = moves.index(move_to_select)
    num_moves = len(moves)
    forward = (target_index - cursor_index + num_moves) % num_moves
    backward = (cursor_index - target_index + num_moves) % num_moves
    buttons = []
    if forward <= backward:
        for _ in range(forward):
            buttons.extend(["Down", "sleep 250"])
    else:
        for _ in range(backward):
            buttons.extend(["Up", "sleep 250"])
    if buttons:
        buttons.pop()
    print(json.dumps(buttons))

