import json
import re

current_screen_text = input_data['current_screen_text']
target_pokemon_name = input_data['target_pokemon_name']

pokemon_names = []
cursor_index = -1
lines = current_screen_text.split('\n')

for line in lines:
    stripped_line = line.strip()

    is_pokemon_entry = '/' in stripped_line
    is_cancel_entry = stripped_line == 'CANCEL'

    if is_pokemon_entry or is_cancel_entry:
        has_cursor = stripped_line.startswith('▶')
        content_line = stripped_line.lstrip('▶').strip()

        name = ""
        if is_cancel_entry:
            name = "CANCEL"
        else:
            match = re.search(r'([A-Z\s]+)\s*\d+/\s*\d+', content_line)
            if match:
                name = match.group(1).strip()

        if name:
            pokemon_names.append(name)
            if has_cursor:
                cursor_index = len(pokemon_names) - 1

if not pokemon_names or target_pokemon_name not in pokemon_names or cursor_index == -1:
    print(json.dumps([]))
else:
    target_index = pokemon_names.index(target_pokemon_name)
    num_items = len(pokemon_names)

    forward = (target_index - cursor_index + num_items) % num_items
    backward = (cursor_index - target_index + num_items) % num_items

    buttons = []
    if forward <= backward:
        for _ in range(forward):
            buttons.append("Down")
    else:
        for _ in range(backward):
            buttons.append("Up")

    buttons.extend(["A", "sleep 500", "A"])

    print(json.dumps(buttons))

