import json
import xml.etree.ElementTree as ET
from collections import deque

start_x = int(input_data['start_x'])
start_y = int(input_data['start_y'])
end_x = int(input_data['end_x'])
end_y = int(input_data['end_y'])
is_surfing = input_data['is_surfing'].lower() == 'true'

root = ET.fromstring(map_xml_string)
width = int(root.get('width'))
height = int(root.get('height'))

grid_types = [['' for _ in range(width)] for _ in range(height)]
grid_objects = [[False for _ in range(width)] for _ in range(height)]

impassable_walking = {'WALL', 'WATER', 'VOID', 'unseen', 'unknown', 'CUT_TREE', 'HEADBUTT_TREE', 'BUOY', 'MART_SHELF', 'PC', 'COUNTER'}
impassable_surfing = {'WALL', 'FLOOR', 'GRASS', 'TALL_GRASS', 'VOID', 'unseen', 'unknown', 'CUT_TREE', 'HEADBUTT_TREE'}
impassable_tiles = impassable_surfing if is_surfing else impassable_walking

for y, row_element in enumerate(root.findall('Row')):
    for x, tile_element in enumerate(row_element.findall('Tile')):
        grid_types[y][x] = tile_element.get('type')
        grid_objects[y][x] = tile_element.get('has-object') == 'true'

def get_simple_neighbors(x, y):
    neighbors = []
    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        nx, ny = x + dx, y + dy
        if 0 <= nx < width and 0 <= ny < height:
            neighbors.append((nx, ny))
    return neighbors

queue = deque([[(start_x, start_y)]])
visited = set([(start_x, start_y)])

target_tiles = set()
is_destination_impassable = (grid_types[end_y][end_x] in impassable_tiles) or grid_objects[end_y][end_x]

if is_destination_impassable:
    # If destination is blocked, find all traversable adjacent tiles to target
    for tx, ty in get_simple_neighbors(end_x, end_y):
        is_adj_impassable = (grid_types[ty][tx] in impassable_tiles) or grid_objects[ty][tx]
        if not is_adj_impassable:
            target_tiles.add((tx, ty))

    if not target_tiles:
        print(json.dumps({'status': 'error', 'message': 'No traversable tile adjacent to destination'}))
        exit()
else:
    # If destination is fine, it's the only target
    target_tiles.add((end_x, end_y))

path_found = False
while queue:
    path = queue.popleft()
    x, y = path[-1]

    if (x, y) in target_tiles:
        path_found = True
        print(json.dumps({'status': 'success', 'path': [{'x': px, 'y': py} for px, py in path]}))
        break

    for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        nx, ny = x + dx, y + dy

        if not (0 <= nx < width and 0 <= ny < height):
            continue

        current_tile_type = grid_types[y][x]
        neighbor_tile_type = grid_types[ny][nx]

        # BUGFIX: Added rules to restrict movement FROM one-way tiles (ledges).
        # The original code only restricted movement TO them.
        if current_tile_type == 'LEDGE_HOP_DOWN' and dy != 1:
            continue
        if current_tile_type == 'LEDGE_HOP_LEFT' and dx != -1:
            continue
        if current_tile_type == 'LEDGE_HOP_RIGHT' and dx != 1:
            continue

        # Rules for moving TO special tiles
        if current_tile_type == 'WATER' and neighbor_tile_type == 'FLOOR_UP_WALL' and dy == 1:
            continue
        if neighbor_tile_type == 'LEDGE_HOP_DOWN' and dy == -1:
            continue
        if neighbor_tile_type == 'LEDGE_HOP_LEFT' and dx == 1:
            continue
        if neighbor_tile_type == 'LEDGE_HOP_RIGHT' and dx == -1:
            continue

        is_neighbor_impassable = (neighbor_tile_type in impassable_tiles) or grid_objects[ny][nx]

        if (nx, ny) not in visited and not is_neighbor_impassable:
            visited.add((nx, ny))
            new_path = list(path)
            new_path.append((nx, ny))
            queue.append(new_path)

if not path_found:
    print(json.dumps({'status': 'error', 'message': 'No path found'}))
