import json
grid = {"New Bark Town": (5, 4), "Cherrygrove City": (4, 4), "Violet City": (4, 3), "Ecruteak City": (3, 3), "Goldenrod City": (3, 4), "Azalea Town": (3, 5), "Olivine City": (2, 4), "Cianwood City": (1, 4), "Mahogany Town": (5, 3), "Indigo Plateau": (5, 2), "Blackthorn City": (6, 3)}
target = input_data['target_city']
if target not in grid: print(json.dumps([])); exit()
x1, y1 = (5, 4)
x2, y2 = grid[target]
dx, dy = x2 - x1, y2 - y1
buttons = ["B", "B", "Start"]
buttons.extend(["Up"] * 8)
buttons.append("Down")
buttons.extend(["A", "A", "A", "sleep 2000"])
if dx > 0:
    for _ in range(dx): buttons.extend(["Right", "sleep 300"])
elif dx < 0:
    for _ in range(abs(dx)): buttons.extend(["Left", "sleep 300"])
if dy > 0:
    for _ in range(dy): buttons.extend(["Down", "sleep 300"])
elif dy < 0:
    for _ in range(abs(dy)): buttons.extend(["Up", "sleep 300"])
buttons.append("A")
final = []
for b in buttons:
    if b.startswith("sleep") and final and final[-1].startswith("sleep"): continue
    final.append(b)
print(json.dumps(final))
