import xml.etree.ElementTree as ET
from collections import deque
import json

def get_path(start_x, start_y, end_x, end_y, map_xml):
    root = ET.fromstring(map_xml)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            tile_type = tile.attrib['type']
            # Objects and Walls are obstacles
            is_blocked = tile_type == 'WALL' or tile.get('has-object') == 'true'
            # Exception for start and end tiles
            if (x, y) == (end_x, end_y) or (x, y) == (start_x, start_y):
                is_blocked = False
            grid[(x, y)] = is_blocked

    queue = deque([(start_x, start_y, [])])
    visited = {(start_x, start_y)}

    while queue:
        curr_x, curr_y, path = queue.popleft()
        if (curr_x, curr_y) == (end_x, end_y):
            # Include start and end in the coordinate path
            full_path = [{"x": start_x, "y": start_y}] + path + [{"x": curr_x, "y": curr_y}]
            # Remove consecutive duplicates if any
            result = []
            for p in full_path:
                if not result or p != result[-1]:
                    result.append(p)
            return result

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = curr_x + dx, curr_y + dy
            if 0 <= nx < width and 0 <= ny < height:
                if (nx, ny) not in visited and not grid.get((nx, ny), True):
                    visited.add((nx, ny))
                    queue.append((nx, ny, path + [{"x": curr_x, "y": curr_y}]))
    return None

