import xml.etree.ElementTree as ET
import heapq
import json

root = ET.fromstring(map_xml_string)
width = int(root.attrib['width'])
height = int(root.attrib['height'])
player_x, player_y = -1, -1

for r in root.findall('Row'):
    for t in r.findall('Tile'):
        if t.find('Player') is not None:
            player_x = int(t.attrib['id'])
            player_y = int(r.attrib['id'])
            break
    if player_x != -1:
        break

impassable_tiles = {'WALL', 'WINDOW', 'CUT_TREE', 'SIGN', 'BOOKSHELF', 'BLACKBOARD', 'MART_SHELF', 'BUOY', 'TV', 'TOWN_MAP', 'BIRD', 'HEADBUTT_TREE', 'FRUIT_TREE', 'COMPUTER', 'PRINTER', 'VOID', 'WATER', 'CAVE', 'COUNTER'}
ledge_down = {'LEDGE_HOP_DOWN'}
ledge_right = {'LEDGE_HOP_RIGHT'}
ledge_left = {'LEDGE_HOP_LEFT'}
floor_up_wall = {'FLOOR_UP_WALL'}

grid = {}
for r in root.findall('Row'):
    y = int(r.attrib['id'])
    for t in r.findall('Tile'):
        x = int(t.attrib['id'])
        tile_type = t.attrib.get('type')
        is_impassable = tile_type in impassable_tiles or t.find('Object') is not None
        grid[(x, y)] = (tile_type, is_impassable)

def get_neighbors(x, y):
    neighbors = []
    current_tile_type, _ = grid.get((x, y), (None, True))
    for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
        nx, ny = x + dx, y + dy
        if not (1 <= nx <= width and 1 <= ny <= height):
            continue
        neighbor_tile_type, is_impassable = grid.get((nx, ny), (None, True))
        if is_impassable:
            continue
        if neighbor_tile_type in ledge_down and move != 'Down': continue
        if neighbor_tile_type in ledge_left and move != 'Left': continue
        if neighbor_tile_type in ledge_right and move != 'Right': continue
        if neighbor_tile_type in floor_up_wall and move != 'Up': continue
        if current_tile_type in floor_up_wall and move != 'Down': continue
        neighbors.append((nx, ny))
    return neighbors

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

try:
    target_data = json.loads(input_data['target_coordinates'])
except json.JSONDecodeError:
    print(json.dumps({"error": "Invalid JSON in target_coordinates"}))
    exit()

if isinstance(target_data, dict):
    target_list = [target_data]
elif isinstance(target_data, list):
    target_list = target_data
else:
    print(json.dumps({"error": "target_coordinates must be a JSON object or a list of JSON objects"}))
    exit()

start_node = (player_x, player_y)
reachable_tiles = []

for target in target_list:
    try:
        target_x = int(target['x'])
        target_y = int(target['y'])
    except (KeyError, TypeError, ValueError):
        continue

    target_node = (target_x, target_y)
    goal_node = target_node
    if grid.get(goal_node, (None, True))[1]:
        possible_goals = [n for n in get_neighbors(goal_node[0], goal_node[1]) if not grid.get(n, (None, True))[1]]
        if not possible_goals:
            continue
        goal_node = min(possible_goals, key=lambda pos: heuristic(start_node, pos))

    open_set = []
    heapq.heappush(open_set, (0, start_node))
    came_from = {}
    g_score = {start_node: 0}

    path_found = False
    while open_set:
        _, current = heapq.heappop(open_set)

        if current == goal_node:
            path_found = True
            break

        for neighbor in get_neighbors(current[0], current[1]):
            tentative_g_score = g_score[current] + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score = g_score[neighbor] + heuristic(neighbor, goal_node)
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score, neighbor))

    if path_found:
        reachable_tiles.append(target)

if isinstance(target_data, dict):
    print(json.dumps(len(reachable_tiles) > 0))
else:
    print(json.dumps(reachable_tiles))

